          vehicles={vehicles} 
        />
      )}

      {activeTab === 'analytics' && (
        <Analytics allTrips={todaysSchedule} routes={routes} />
      )}

      {activeTab === 'settings' && (
        <SystemSettings />
      )}

      {/* Route Details Modal */}
      {showRouteDetails && (
        <div style={{ zIndex: 9999 }}>
          <RouteDetails
            route={detailRoute}
            locations={locations}
            onClose={() => {
              setShowRouteDetails(false);
              setDetailRoute(null);
            }}
          />
        </div>
      )}
