  );
};

// Vehicle Management Component
const VehicleManagement = ({ vehicles = [], onRefresh }) => {
  const [showAddVehicle, setShowAddVehicle] = useState(false);
  const [editingVehicle, setEditingVehicle] = useState(null);
  const [showServiceModal, setShowServiceModal] = useState(null);

  const handleAddVehicle = () => {
    setShowAddVehicle(true);
  };

  const handleEditVehicle = (vehicle) => {
    setEditingVehicle({
      ...vehicle,
      editCapacity: vehicle.vehicle_capacity || vehicle.capacity || 0,
      editMileage: vehicle.current_mileage || 0,
      editLastService: vehicle.last_service_date || ''
    });
  };

  const handleServiceVehicle = async (vehicle) => {
    try {
      // Toggle between active and maintenance status
      const newStatus = vehicle.status === 'active' ? 'maintenance' : 'active';

      const response = await fetch(`http://localhost:5001/api/admin/vehicles/${vehicle.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ status: newStatus }),
      });

      if (response.ok) {
        setShowServiceModal({
          ...vehicle,
          newStatus,
          message: newStatus === 'maintenance' 
            ? 'Vehicle has been marked for maintenance' 
            : 'Vehicle has been returned to active service'
        });
        // Refresh data after a short delay
        setTimeout(() => {
          onRefresh();
        }, 1500);
      } else {
        alert('Failed to update vehicle status');
      }
    } catch (error) {
      console.error('Error updating vehicle status:', error);
      alert('Error updating vehicle status');
    }
  };

  const handleSaveEdit = async () => {
    try {
      const updateData = {
        current_mileage: editingVehicle.editMileage,
        last_service_date: editingVehicle.editLastService || null
      };

      // Update capacity through vehicle_types if needed
      if (editingVehicle.editCapacity !== editingVehicle.vehicle_capacity) {
        // For now, we'll skip capacity update as it's tied to vehicle_type
        // In a real scenario, you'd either update vehicle_type or create new type
      }

      const response = await fetch(`http://localhost:5001/api/admin/vehicles/${editingVehicle.id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updateData),
      });

      if (response.ok) {
        setEditingVehicle(null);
        onRefresh();
        alert('Vehicle updated successfully!');
      } else {
        alert('Failed to update vehicle');
      }
    } catch (error) {
      console.error('Error updating vehicle:', error);
      alert('Error updating vehicle');
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Vehicle Management</h2>
        <button 
          className="btn-primary flex items-center"
          onClick={handleAddVehicle}
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Vehicle
        </button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {vehicles.map((vehicle) => (
          <div key={vehicle.id} className="card">
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                  <Bus className="w-5 h-5 text-blue-600" />
                </div>
                <div>
                  <h3 className="font-semibold text-gray-900">{vehicle.license_plate || vehicle.plateNumber || vehicle.vehicle_number}</h3>
                  <p className="text-sm text-gray-600">{vehicle.model || 'N/A'}</p>
                </div>
              </div>
              <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                vehicle.status === 'active' ? 'bg-green-100 text-green-800' :
                vehicle.status === 'maintenance' ? 'bg-yellow-100 text-yellow-800' :
                'bg-red-100 text-red-800'
              }`}>
                {vehicle.status}
              </span>
            </div>

            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-600">Capacity:</span>
                <span className="font-medium">{vehicle.vehicle_capacity || vehicle.capacity || 'N/A'} seats</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Mileage:</span>
                <span className="font-medium">{vehicle.current_mileage ? vehicle.current_mileage.toLocaleString() : 'N/A'} km</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Last Service:</span>
                <span className="font-medium">
                  {vehicle.last_service_date 
                    ? new Date(vehicle.last_service_date).toLocaleDateString('en-GB')
                    : vehicle.lastService || 'N/A'}
                </span>
              </div>
            </div>

            <div className="mt-4 flex space-x-2">
              <button 
                className="btn-secondary text-xs flex-1"
                onClick={() => handleEditVehicle(vehicle)}
              >
                Edit
              </button>
              <button 
                className="btn-secondary text-xs flex-1"
                onClick={() => handleServiceVehicle(vehicle)}
              >
                Service
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Add Vehicle Modal */}
      {showAddVehicle && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Add New Vehicle</h3>
            <p className="text-gray-600 mb-4">Add vehicle functionality coming soon...</p>
            <button 
              className="btn-secondary w-full"
              onClick={() => setShowAddVehicle(false)}
            >
              Close
            </button>
          </div>
        </div>
      )}

      {/* Edit Vehicle Modal */}
      {editingVehicle && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Edit Vehicle: {editingVehicle.license_plate}</h3>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Capacity (seats)
                </label>
                <input
                  type="number"
                  value={editingVehicle.editCapacity}
                  onChange={(e) => setEditingVehicle(prev => ({
                    ...prev,
                    editCapacity: parseInt(e.target.value)
                  }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  min="1"
                  max="100"
                  disabled
                />
                <p className="text-xs text-gray-500 mt-1">Capacity is determined by vehicle type</p>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Current Mileage (km)
                </label>
                <input
                  type="number"
                  value={editingVehicle.editMileage}
                  onChange={(e) => setEditingVehicle(prev => ({
                    ...prev,
                    editMileage: parseInt(e.target.value)
                  }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  min="0"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Last Service Date
                </label>
                <input
                  type="date"
                  value={editingVehicle.editLastService}
                  onChange={(e) => setEditingVehicle(prev => ({
                    ...prev,
                    editLastService: e.target.value
                  }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            <div className="flex space-x-3 mt-6">
              <button 
                className="btn-primary flex-1"
                onClick={handleSaveEdit}
              >
                Save Changes
              </button>
              <button 
                className="btn-secondary flex-1"
                onClick={() => setEditingVehicle(null)}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Service Modal */}
      {showServiceModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Service Status: {showServiceModal.license_plate}</h3>

            <div className="text-center mb-6">
              <div className={`inline-flex items-center px-4 py-2 rounded-full text-sm font-medium ${
                showServiceModal.newStatus === 'maintenance' 
                  ? 'bg-yellow-100 text-yellow-800' 
                  : 'bg-green-100 text-green-800'
              }`}>
                Status: {showServiceModal.newStatus === 'maintenance' ? 'Under Maintenance' : 'Active Service'}
              </div>

              <p className="text-gray-600 mt-4">{showServiceModal.message}</p>

              {showServiceModal.newStatus === 'maintenance' && (
                <p className="text-sm text-yellow-600 mt-2">
                  Vehicle is now out of service. Click Service button again to return to active status.
                </p>
              )}
            </div>

            <button 
              className="btn-secondary w-full"
              onClick={() => setShowServiceModal(null)}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

// Schedule Management Component
const ScheduleManagement = ({ todaysSchedule = [], routes = [], vehicles = [] }) => {
  const [selectedDate, setSelectedDate] = useState(format(new Date(), 'yyyy-MM-dd'));
  const [filteredSchedule, setFilteredSchedule] = useState([]);

  // Filter schedule by selected date
  useEffect(() => {
    if (selectedDate === format(new Date(), 'yyyy-MM-dd')) {
      setFilteredSchedule(todaysSchedule);
    } else {
      // Here you would fetch schedule for selected date
      // For now, just show today's schedule
      setFilteredSchedule(todaysSchedule);
    }
  }, [selectedDate, todaysSchedule]);

  const formatTime = (datetime) => {
    try {
      return new Date(datetime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
    } catch (error) {
      return 'Invalid time';
    }
  };

  const formatDate = (datetime) => {
    try {
      return new Date(datetime).toLocaleDateString('en-GB');
    } catch (error) {
      return 'Invalid date';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'scheduled': return 'bg-blue-100 text-blue-800';
      case 'boarding': return 'bg-yellow-100 text-yellow-800';
      case 'in_transit': return 'bg-green-100 text-green-800';
      case 'arrived': return 'bg-gray-100 text-gray-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      case 'delayed': return 'bg-orange-100 text-orange-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Schedule Management</h2>
        <div className="flex space-x-3">
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            min={format(new Date(), 'yyyy-MM-dd')}
            max={format(addDays(new Date(), 90), 'yyyy-MM-dd')}
          />
          <button className="btn-primary flex items-center">
            <Calendar className="w-4 h-4 mr-2" />
            Create Schedule
          </button>
        </div>
      </div>

      <div className="card">
        <h3 className="text-lg font-semibold mb-4">
          Schedule for {formatDate(selectedDate)}
          <span className="ml-2 text-sm font-normal text-gray-600">
            ({filteredSchedule.length} trips)
          </span>
        </h3>

        {filteredSchedule.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            <Calendar className="w-12 h-12 mx-auto mb-3 text-gray-300" />
            <p>No trips scheduled for this date</p>
          </div>
        ) : (
          <div className="space-y-3">
            {filteredSchedule.map((trip) => {
              const occupiedSeats = trip.occupied_seats || (trip.total_seats - trip.available_seats);
              const occupancyPercentage = trip.occupancy_percentage || 
                Math.round((occupiedSeats / trip.total_seats) * 100);

              return (
                <div key={trip.id} className="p-4 border border-gray-200 rounded-lg hover:bg-gray-50">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-6">
                        {/* Route Info */}
                        <div className="min-w-0 flex-1">
                          <div className="font-medium text-gray-900 mb-1">
                            {trip.route_name}
                          </div>
                          <div className="text-sm text-gray-600">
                            {trip.origin_name}  {trip.destination_name}
                          </div>
                        </div>

                        {/* Vehicle Info */}
                        <div className="text-sm text-gray-600">
                          <div className="font-medium">{trip.vehicle_number}</div>
                          <div>{trip.vehicle_model}</div>
                        </div>

                        {/* Time Info */}
                        <div className="text-sm text-gray-600">
                          <div className="font-medium">
                            {formatTime(trip.departure_datetime)}
                          </div>
                          <div>
                            to {formatTime(trip.estimated_arrival_datetime)}
                          </div>
                        </div>

                        {/* Status */}
                        <div>
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(trip.status)}`}>
                            {trip.status.replace('_', ' ').toUpperCase()}
                          </span>
                        </div>
                      </div>
                    </div>

                    {/* Occupancy Info */}
                    <div className="flex items-center space-x-4 ml-4">
                      <div className="text-sm text-right">
                        <div className="font-medium">
                          {occupiedSeats}/{trip.total_seats} seats
                        </div>
                        <div className={`text-xs px-2 py-1 rounded-full ${
                          occupancyPercentage > 80 ? 'bg-red-100 text-red-600' :
                          occupancyPercentage > 60 ? 'bg-yellow-100 text-yellow-600' :
                          'bg-green-100 text-green-600'
                        }`}>
                          {occupancyPercentage}% occupied
                        </div>
                      </div>
                      <div className="w-16 bg-gray-200 rounded-full h-2">
                        <div 
                          className="h-2 rounded-full transition-all"
                          style={{ 
                            width: `${Math.min(occupancyPercentage, 100)}%`,
                            backgroundColor: occupancyPercentage > 80 ? '#EF4444' : 
                              occupancyPercentage > 60 ? '#F59E0B' : '#10B981'
                          }}
                        ></div>
                      </div>

                      {/* Action Buttons */}
                      <div className="flex space-x-2">
                        <button className="p-2 text-gray-600 hover:text-blue-600 hover:bg-blue-50 rounded">
                          <Edit className="w-4 h-4" />
                        </button>
                        <button className="p-2 text-gray-600 hover:text-red-600 hover:bg-red-50 rounded">
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    </div>
  );
};

// Analytics Component
const Analytics = ({ allTrips, routes }) => {
  const totalRevenue = allTrips.reduce((sum, trip) => {
    const route = routes.find(r => r.id === trip.routeId);
    return sum + (route?.basePrice || 0) * calculateOccupancy(trip);
  }, 0);

  const avgOccupancy = allTrips.length > 0 
    ? allTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0) / allTrips.length
    : 0;

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold text-gray-900">Analytics & Reports</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Total Revenue</p>
              <p className="text-2xl font-bold text-green-600">RM {totalRevenue.toLocaleString()}</p>
            </div>
            <TrendingUp className="w-8 h-8 text-green-600" />
          </div>
        </div>

        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Average Occupancy</p>
              <p className="text-2xl font-bold text-blue-600">{avgOccupancy.toFixed(1)}%</p>
            </div>
            <Users className="w-8 h-8 text-blue-600" />
          </div>
        </div>

        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Total Passengers</p>
              <p className="text-2xl font-bold text-purple-600">{allTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0)}</p>
            </div>
            <Users className="w-8 h-8 text-purple-600" />
          </div>
        </div>
      </div>

      <div className="card">
        <h3 className="text-lg font-semibold mb-4">Route Performance</h3>
        <div className="space-y-4">
          {routes.map((route) => {
            const routeTrips = allTrips.filter(trip => trip.routeId === route.id);
            const routeRevenue = routeTrips.reduce((sum, trip) => sum + route.basePrice * calculateOccupancy(trip), 0);
            const routeOccupancy = routeTrips.length > 0 
              ? routeTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0) / routeTrips.length
              : 0;

            return (
              <div key={route.id} className="p-4 border border-gray-200 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <h4 className="font-medium text-gray-900">{route.name}</h4>
                    <p className="text-sm text-gray-600">{routeTrips.length} trips scheduled</p>
                  </div>
                  <div className="text-right space-y-1">
                    <div className="text-sm font-medium text-green-600">RM {routeRevenue.toLocaleString()}</div>
                    <div className="flex items-center space-x-2">
                      <div className="w-20 bg-gray-200 rounded-full h-2">
                        <div 
                          className="h-2 rounded-full transition-all"
                          style={{ 
                            width: `${Math.min(routeOccupancy, 100)}%`,
                            backgroundColor: routeOccupancy > 80 ? '#EF4444' : routeOccupancy > 60 ? '#F59E0B' : '#10B981'
                          }}
                        ></div>
                      </div>
                      <span className="text-xs font-medium">{routeOccupancy.toFixed(0)}%</span>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

