import React, { useState, useEffect } from 'react';
import { 
  MapPin, 
  Plus, 
  Pencil, 
  Trash2, 
  Search,
  X,
  CheckCircle
} from 'lucide-react';
import { 
  getAllLocations, 
  createLocation, 
  updateLocation, 
  deleteLocation 
} from '../../services/adminApi';

const LocationManagement = () => {
  const [locations, setLocations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingLocation, setEditingLocation] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    city: '',
    address: '',
    type: 'terminal', // terminal, stop, office
    latitude: '',
    longitude: '',
    facilities: {
      hasWaitingRoom: false,
      hasRestrooms: false,
      hasFood: false,
      hasWifi: false,
      isAccessible: false
    }
  });

  // 加载所有站点
  const fetchLocations = async () => {
    setLoading(true);
    try {
      const response = await getAllLocations();
      if (response.success) {
        setLocations(response.data);
      } else {
        setError(response.message || 'Failed to load locations');
      }
    } catch (err) {
      setError('Error connecting to server');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLocations();
  }, []);

  // 处理表单输入变化
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name.includes('.')) {
      const [section, field] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [section]: {
          ...prev[section],
          [field]: type === 'checkbox' ? checked : value
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  // 打开编辑表单
  const handleEdit = (location) => {
    setEditingLocation(location);
    setFormData({
      name: location.name,
      city: location.city,
      address: location.address || '',
      type: location.type || 'terminal',
      latitude: location.latitude || '',
      longitude: location.longitude || '',
      facilities: {
        hasWaitingRoom: location.facilities?.hasWaitingRoom || false,
        hasRestrooms: location.facilities?.hasRestrooms || false,
        hasFood: location.facilities?.hasFood || false,
        hasWifi: location.facilities?.hasWifi || false,
        isAccessible: location.facilities?.isAccessible || false
      }
    });
    setShowForm(true);
  };

  // 添加新站点
  const handleAdd = () => {
    setEditingLocation(null);
    setFormData({
      name: '',
      city: '',
      address: '',
      type: 'terminal',
      latitude: '',
      longitude: '',
      facilities: {
        hasWaitingRoom: false,
        hasRestrooms: false,
        hasFood: false,
        hasWifi: false,
        isAccessible: false
      }
    });
    setShowForm(true);
  };

  // 删除站点
  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this location?')) return;

    setLoading(true);
    try {
      const response = await deleteLocation(id);
      if (response.success) {
        setLocations(locations.filter(loc => loc.id !== id));
      } else {
        setError(response.message || 'Failed to delete location');
      }
    } catch (err) {
      setError('Error connecting to server');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // 提交表单
  const handleSubmit = async (e) => {
    e.preventDefault();

    setLoading(true);
    try {
      let response;

      if (editingLocation) {
        response = await updateLocation(editingLocation.id, formData);
      } else {
        response = await createLocation(formData);
      }

      if (response.success) {
        await fetchLocations();
        setShowForm(false);
      } else {
        setError(response.message || 'Failed to save location');
      }
    } catch (err) {
      setError('Error connecting to server');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // 过滤站点
  const filteredLocations = locations.filter(location => 
    location.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    location.city.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container mx-auto px-4 py-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Station Management</h1>
        <button 
          onClick={handleAdd}
          className="btn-primary flex items-center"
        >
          <Plus className="w-4 h-4 mr-1" />
          Add Station
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="mb-6">
        <div className="relative">
          <input
            type="text"
            placeholder="Search stations by name or city..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full px-4 py-2 pl-10 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <Search className="w-5 h-5 text-gray-400 absolute left-3 top-2.5" />
          {searchTerm && (
            <button
              onClick={() => setSearchTerm('')}
              className="absolute right-3 top-2.5"
            >
              <X className="w-5 h-5 text-gray-400" />
            </button>
          )}
        </div>
      </div>

      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-md">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">
                {editingLocation ? 'Edit Station' : 'Add New Station'}
              </h2>
              <button onClick={() => setShowForm(false)}>
                <X className="w-5 h-5" />
              </button>
            </div>

            <form onSubmit={handleSubmit} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Station Name
                </label>
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  City
                </label>
                <input
                  type="text"
                  name="city"
                  value={formData.city}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Address
                </label>
                <input
                  type="text"
                  name="address"
                  value={formData.address}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Type
                </label>
                <select
                  name="type"
                  value={formData.type}
                  onChange={handleInputChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="terminal">Bus Terminal</option>
                  <option value="stop">Bus Stop</option>
                  <option value="office">Ticket Office</option>
                </select>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Latitude
                  </label>
                  <input
                    type="text"
                    name="latitude"
                    value={formData.latitude}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="e.g. 3.1390"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Longitude
                  </label>
                  <input
                    type="text"
                    name="longitude"
                    value={formData.longitude}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="e.g. 101.6869"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Facilities
                </label>
                <div className="space-y-2">
                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      id="hasWaitingRoom"
                      name="facilities.hasWaitingRoom"
                      checked={formData.facilities.hasWaitingRoom}
                      onChange={handleInputChange}
                      className="mr-2"
                    />
                    <label htmlFor="hasWaitingRoom" className="text-sm">
                      Waiting Room
                    </label>
                  </div>

                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      id="hasRestrooms"
                      name="facilities.hasRestrooms"
                      checked={formData.facilities.hasRestrooms}
                      onChange={handleInputChange}
                      className="mr-2"
                    />
                    <label htmlFor="hasRestrooms" className="text-sm">
                      Restrooms
                    </label>
                  </div>

                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      id="hasFood"
                      name="facilities.hasFood"
                      checked={formData.facilities.hasFood}
                      onChange={handleInputChange}
                      className="mr-2"
                    />
                    <label htmlFor="hasFood" className="text-sm">
                      Food & Beverages
                    </label>
                  </div>

                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      id="hasWifi"
                      name="facilities.hasWifi"
                      checked={formData.facilities.hasWifi}
                      onChange={handleInputChange}
                      className="mr-2"
                    />
                    <label htmlFor="hasWifi" className="text-sm">
                      Free WiFi
                    </label>
                  </div>

                  <div className="flex items-center">
                    <input
                      type="checkbox"
                      id="isAccessible"
                      name="facilities.isAccessible"
                      checked={formData.facilities.isAccessible}
                      onChange={handleInputChange}
                      className="mr-2"
                    />
                    <label htmlFor="isAccessible" className="text-sm">
                      Wheelchair Accessible
                    </label>
                  </div>
                </div>
              </div>

              <div className="flex justify-end space-x-4 pt-4">
                <button
                  type="button"
                  onClick={() => setShowForm(false)}
                  className="btn-secondary"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="btn-primary"
                  disabled={loading}
                >
                  {loading ? 'Saving...' : 'Save Station'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {loading && !showForm ? (
        <div className="text-center py-10">
          <div className="spinner"></div>
          <p className="mt-2 text-gray-600">Loading stations...</p>
        </div>
      ) : (
        <div className="bg-white shadow rounded-lg overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Name
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  City
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Type
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Facilities
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredLocations.length === 0 ? (
                <tr>
                  <td colSpan="5" className="px-6 py-4 text-center text-gray-500">
                    No stations found
                  </td>
                </tr>
              ) : (
                filteredLocations.map(location => (
                  <tr key={location.id}>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex items-center">
                        <MapPin className="w-5 h-5 mr-2 text-blue-500" />
                        <div>
                          <div className="font-medium text-gray-900">{location.name}</div>
                          {location.address && (
                            <div className="text-xs text-gray-500">{location.address}</div>
                          )}
                        </div>
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-gray-500">
                      {location.city}
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                        ${location.type === 'terminal' ? 'bg-green-100 text-green-800' : 
                          location.type === 'stop' ? 'bg-blue-100 text-blue-800' : 
                          'bg-purple-100 text-purple-800'}`}
                      >
                        {location.type === 'terminal' ? 'Bus Terminal' : 
                          location.type === 'stop' ? 'Bus Stop' : 'Ticket Office'}
                      </span>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap">
                      <div className="flex space-x-1">
                        {location.facilities?.hasWaitingRoom && (
                          <span className="w-6 h-6 flex items-center justify-center bg-blue-100 text-blue-800 rounded-full" title="Waiting Room">
                            W
                          </span>
                        )}
                        {location.facilities?.hasRestrooms && (
                          <span className="w-6 h-6 flex items-center justify-center bg-green-100 text-green-800 rounded-full" title="Restrooms">
                            R
                          </span>
                        )}
                        {location.facilities?.hasFood && (
                          <span className="w-6 h-6 flex items-center justify-center bg-yellow-100 text-yellow-800 rounded-full" title="Food & Beverages">
                            F
                          </span>
                        )}
                        {location.facilities?.hasWifi && (
                          <span className="w-6 h-6 flex items-center justify-center bg-purple-100 text-purple-800 rounded-full" title="Free WiFi">
                            Wi
                          </span>
                        )}
                        {location.facilities?.isAccessible && (
                          <span className="w-6 h-6 flex items-center justify-center bg-red-100 text-red-800 rounded-full" title="Wheelchair Accessible">
                            A
                          </span>
                        )}
                      </div>
                    </td>
                    <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                      <button
                        onClick={() => handleEdit(location)}
                        className="text-blue-600 hover:text-blue-900 mr-4"
                      >
                        <Pencil className="w-4 h-4" />
                      </button>
                      <button
                        onClick={() => handleDelete(location.id)}
                        className="text-red-600 hover:text-red-900"
                      >
                        <Trash2 className="w-4 h-4" />
                      </button>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default LocationManagement;
