import React from 'react';
import { format } from 'date-fns';
import { 
  MapPin, 
  Calendar, 
  Clock, 
  Users, 
  CreditCard,
  Bus,
  Phone,
  Mail,
  CheckCircle
} from 'lucide-react';
import { useApp } from '../context/AppContext';

const BookingReceipt = ({ booking, trip, seats, passengerInfo, onClose }) => {
  const { systemDate } = useApp();
  const currentDate = new Date(systemDate);

  const handlePrint = () => {
    window.print();
  };

  const handleDownload = () => {
    // Create a new window for PDF generation
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <!DOCTYPE html>
      <html>
        <head>
          <title>Booking Receipt - ${booking.bookingReference}</title>
          <style>
            * { margin: 0; padding: 0; box-sizing: border-box; }
            body { font-family: 'Arial', sans-serif; line-height: 1.6; color: #333; }
            .receipt-container { max-width: 800px; margin: 0 auto; padding: 20px; }
            .header { text-align: center; border-bottom: 2px solid #2563eb; padding-bottom: 20px; margin-bottom: 30px; }
            .company-logo { font-size: 28px; font-weight: bold; color: #2563eb; margin-bottom: 10px; }
            .receipt-title { font-size: 24px; font-weight: bold; margin-bottom: 10px; }
            .receipt-subtitle { color: #666; font-size: 16px; }
            .section { margin-bottom: 25px; }
            .section-title { font-size: 18px; font-weight: bold; color: #2563eb; margin-bottom: 15px; border-bottom: 1px solid #e5e7eb; padding-bottom: 5px; }
            .info-grid { display: grid; grid-template-columns: 1fr 1fr; gap: 20px; margin-bottom: 20px; }
            .info-item { display: flex; align-items: center; margin-bottom: 10px; }
            .info-label { font-weight: bold; margin-right: 10px; min-width: 120px; }
            .route-section { background: #f8fafc; padding: 20px; border-radius: 8px; margin-bottom: 20px; }
            .route-header { display: flex; justify-content: space-between; align-items: center; margin-bottom: 15px; }
            .route-cities { display: flex; align-items: center; justify-content: center; margin-bottom: 15px; }
            .city { text-align: center; flex: 1; }
            .city-name { font-size: 20px; font-weight: bold; }
            .city-time { color: #666; font-size: 14px; }
            .arrow { margin: 0 20px; font-size: 24px; color: #2563eb; }
            .seats-section { background: #f0f9ff; padding: 15px; border-radius: 8px; margin-bottom: 20px; }
            .seat-item { display: inline-block; background: #2563eb; color: white; padding: 5px 10px; border-radius: 15px; margin: 5px; font-size: 14px; }
            .payment-section { background: #f0fdf4; padding: 15px; border-radius: 8px; margin-bottom: 20px; }
            .total-amount { font-size: 24px; font-weight: bold; color: #2563eb; text-align: center; padding: 15px; background: #eff6ff; border-radius: 8px; margin: 20px 0; }
            .important-info { background: #fef3c7; padding: 15px; border-radius: 8px; border-left: 4px solid #f59e0b; }
            .important-info ul { list-style: none; }
            .important-info li { margin-bottom: 8px; position: relative; padding-left: 20px; }
            .important-info li:before { content: "â€¢"; color: #f59e0b; font-weight: bold; position: absolute; left: 0; }
            .footer { text-align: center; margin-top: 30px; padding-top: 20px; border-top: 1px solid #e5e7eb; color: #666; font-size: 14px; }
            .qr-section { text-align: center; margin: 20px 0; }
            .booking-ref { font-size: 18px; font-weight: bold; background: #eff6ff; padding: 10px; border-radius: 8px; display: inline-block; }
            @media print {
              body { -webkit-print-color-adjust: exact; }
              .receipt-container { max-width: none; margin: 0; padding: 15px; }
              .no-print { display: none !important; }
            }
          </style>
        </head>
        <body>
          <div class="receipt-container">
            ${document.getElementById('receipt-content').innerHTML}
          </div>
        </body>
      </html>
    `);
    printWindow.document.close();
    printWindow.print();
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header Controls */}
        <div className="flex justify-between items-center p-6 border-b no-print">
          <h2 className="text-xl font-semibold">Booking Receipt</h2>
          <div className="flex space-x-3">
            <button
              onClick={handlePrint}
              className="btn-primary flex items-center space-x-2"
            >
              <span>Print</span>
            </button>
            <button
              onClick={handleDownload}
              className="btn-secondary flex items-center space-x-2"
            >
              <span>Download PDF</span>
            </button>
            <button
              onClick={onClose}
              className="btn-outline"
            >
              Close
            </button>
          </div>
        </div>

        {/* Receipt Content */}
        <div id="receipt-content" className="p-8">
          {/* Header */}
          <div className="text-center border-b-2 border-primary-600 pb-6 mb-8">
            <div className="text-3xl font-bold text-primary-600 mb-2">ðŸšŒ TransportBook</div>
            <div className="text-2xl font-bold mb-2">BOOKING RECEIPT</div>
            <div className="text-gray-600">Official Travel Document</div>
            <div className="mt-4 inline-block bg-primary-50 px-4 py-2 rounded-lg">
              <div className="text-lg font-bold text-primary-800">
                Booking Reference: {booking.bookingReference}
              </div>
            </div>
          </div>

          {/* Trip Route Section */}
          <div className="bg-gray-50 p-6 rounded-lg mb-6">
            <h3 className="text-xl font-bold text-primary-600 mb-4 flex items-center">
              <MapPin className="w-5 h-5 mr-2" />
              Trip Details
            </h3>

            <div className="flex items-center justify-center mb-6">
              <div className="text-center flex-1">
                <div className="text-2xl font-bold">{trip.originLocation?.city}</div>
                <div className="text-gray-600 font-medium">{trip.originLocation?.name}</div>
                <div className="text-xs text-gray-500 mt-1">{trip.originLocation?.address}</div>
                <div className="text-sm text-primary-600 font-medium mt-2">
                  {(() => {
                    try {
                      return trip.departureDatetime ? format(new Date(trip.departureDatetime), 'HH:mm') : 'N/A';
                    } catch (error) {
                      return 'N/A';
                    }
                  })()}
                </div>
              </div>

              <div className="mx-8 text-3xl text-primary-600">â†’</div>

              <div className="text-center flex-1">
                <div className="text-2xl font-bold">{trip.destinationLocation?.city}</div>
                <div className="text-gray-600 font-medium">{trip.destinationLocation?.name}</div>
                <div className="text-xs text-gray-500 mt-1">{trip.destinationLocation?.address}</div>
                <div className="text-sm text-primary-600 font-medium mt-2">
                  {(() => {
                    try {
                      const arrivalTime = trip.estimatedArrivalDatetime || trip.arrivalDatetime;
                      return arrivalTime ? format(new Date(arrivalTime), 'HH:mm') : 'N/A';
                    } catch (error) {
                      return 'N/A';
                    }
                  })()}
                </div>
              </div>
            </div>

            <div className="text-center bg-primary-600 text-white py-3 rounded-lg">
              <div className="text-2xl font-bold">RM {booking.totalAmount || trip.price}</div>
              <div className="text-sm">Total Amount</div>
            </div>
          </div>

          {/* Trip Information Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
            <div>
              <h4 className="text-lg font-bold text-primary-600 mb-3 flex items-center">
                <Calendar className="w-5 h-5 mr-2" />
                Travel Information
              </h4>
              <div className="space-y-3">
                <div className="flex">
                  <span className="font-semibold w-32">Travel Date:</span>
                  <span>
                    {(() => {
                      try {
                        return trip.departureDatetime ? format(new Date(trip.departureDatetime), 'EEEE, MMMM dd, yyyy') : 'N/A';
                      } catch (error) {
                        return 'N/A';
                      }
                    })()}
                  </span>
                </div>
                <div className="flex">
                  <span className="font-semibold w-32">Departure:</span>
                  <span>
                    {(() => {
                      try {
                        return trip.departureDatetime ? format(new Date(trip.departureDatetime), 'HH:mm') : 'N/A';
                      } catch (error) {
                        return 'N/A';
                      }
                    })()}
                  </span>
                </div>
                <div className="flex">
                  <span className="font-semibold w-32">Trip Number:</span>
                  <span>{trip.tripNumber || 'N/A'}</span>
                </div>
                <div className="flex">
                  <span className="font-semibold w-32">Vehicle:</span>
                  <span>{trip.vehicleType?.name || 'Luxury Coach'}</span>
                </div>
              </div>
            </div>

            <div>
              <h4 className="text-lg font-bold text-primary-600 mb-3 flex items-center">
                <Users className="w-5 h-5 mr-2" />
                Passenger Information
              </h4>
              <div className="space-y-3">
                <div className="flex">
                  <span className="font-semibold w-32">Name:</span>
                  <span>{passengerInfo?.name || 'N/A'}</span>
                </div>
                <div className="flex">
                  <span className="font-semibold w-32">Email:</span>
                  <span>{passengerInfo?.email || booking.contactEmail || 'N/A'}</span>
                </div>
                <div className="flex">
                  <span className="font-semibold w-32">Phone:</span>
                  <span>{passengerInfo?.phone || booking.contactPhone || 'N/A'}</span>
                </div>
                <div className="flex">
                  <span className="font-semibold w-32">Passengers:</span>
                  <span>{seats?.length || booking.passengerCount || 1}</span>
                </div>
              </div>
            </div>
          </div>

          {/* Selected Seats */}
          {seats && seats.length > 0 && (
            <div className="bg-blue-50 p-4 rounded-lg mb-6">
              <h4 className="text-lg font-bold text-primary-600 mb-3">Selected Seats</h4>
              <div className="flex flex-wrap gap-2">
                {seats.map((seat, index) => (
                  <div key={index} className="bg-primary-600 text-white px-3 py-1 rounded-full text-sm font-medium">
                    Seat {seat.seatNumber} - RM {seat.price}
                  </div>
                ))}
              </div>
            </div>
          )}

          {/* Payment Information */}
          <div className="bg-green-50 p-4 rounded-lg mb-6">
            <h4 className="text-lg font-bold text-primary-600 mb-3 flex items-center">
              <CreditCard className="w-5 h-5 mr-2" />
              Payment Information
            </h4>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <div className="flex justify-between mb-2">
                  <span>Booking Status:</span>
                  <span className="flex items-center text-green-600">
                    <CheckCircle className="w-4 h-4 mr-1" />
                    {booking.bookingStatus || 'Confirmed'}
                  </span>
                </div>
                <div className="flex justify-between mb-2">
                  <span>Payment Status:</span>
                  <span className="flex items-center text-green-600">
                    <CheckCircle className="w-4 h-4 mr-1" />
                    {booking.paymentStatus || 'Paid'}
                  </span>
                </div>
              </div>
              <div>
                <div className="flex justify-between mb-2">
                  <span>Booking Date:</span>
                  <span>{format(currentDate, 'MMM dd, yyyy HH:mm')}</span>
                </div>
                <div className="flex justify-between mb-2">
                  <span>Payment Method:</span>
                  <span>Credit/Debit Card</span>
                </div>
              </div>
            </div>
          </div>

          {/* Important Information */}
          <div className="bg-yellow-50 border-l-4 border-yellow-400 p-4 mb-6">
            <h4 className="text-lg font-bold text-yellow-800 mb-3">Important Information</h4>
            <ul className="space-y-2 text-yellow-800">
              <li className="flex items-start">
                <span className="w-2 h-2 bg-yellow-600 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                <span>Please arrive at the terminal at least 15 minutes before departure</span>
              </li>
              <li className="flex items-start">
                <span className="w-2 h-2 bg-yellow-600 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                <span>Bring a valid ID for boarding verification</span>
              </li>
              <li className="flex items-start">
                <span className="w-2 h-2 bg-yellow-600 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                <span>Free cancellation up to 24 hours before departure</span>
              </li>
              <li className="flex items-start">
                <span className="w-2 h-2 bg-yellow-600 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                <span>Keep this booking reference for your records</span>
              </li>
              <li className="flex items-start">
                <span className="w-2 h-2 bg-yellow-600 rounded-full mt-2 mr-3 flex-shrink-0"></span>
                <span>For assistance, contact us at +60 3-1234 5678 or support@transportbook.com</span>
              </li>
            </ul>
          </div>

          {/* Footer */}
          <div className="text-center border-t pt-6 text-gray-600">
            <div className="mb-2">
              <strong>TransportBook Sdn Bhd</strong>
            </div>
            <div className="text-sm">
              Level 10, Menara TM, Jalan Pantai Baharu, 50672 Kuala Lumpur, Malaysia
            </div>
            <div className="text-sm">
              Tel: +60 3-1234 5678 | Email: support@transportbook.com | Web: www.transportbook.com
            </div>
            <div className="text-sm mt-2">
              Generated on {format(currentDate, 'EEEE, MMMM dd, yyyy \'at\' HH:mm')}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BookingReceipt;
