import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { format } from 'date-fns';
import { 
  MapPin, 
  Clock, 
  Users, 
  ArrowRight, 
  Bus,
  CreditCard,
  ArrowLeft,
  Info,
  CheckCircle,
  AlertCircle
} from 'lucide-react';
import { useApp } from '../context/AppContext';

const TripDetails = () => {
  const { tripId } = useParams();
  const navigate = useNavigate();
  const { 
    selectedTrip, 
    selectedSeat, 
    selectSeat, 
    isAuthenticated, 
    currentUser,
    addNotification 
  } = useApp();

  const [selectedSeats, setSelectedSeats] = useState([]);
  const [passengerInfo, setPassengerInfo] = useState({
    name: currentUser?.firstName + ' ' + currentUser?.lastName || '',
    email: currentUser?.email || '',
    phone: currentUser?.phone || ''
  });

  useEffect(() => {
    if (!selectedTrip) {
      navigate('/search');
      return;
    }

    // Generate seats if not already present
    if (!selectedTrip.seats) {
      generateSeats();
    }

    // Update passenger info when user changes
    if (currentUser) {
      setPassengerInfo({
        name: currentUser.firstName + ' ' + currentUser.lastName,
        email: currentUser.email,
        phone: currentUser.phone || ''
      });
    }
  }, [selectedTrip, navigate, currentUser]);

  const generateSeats = () => {
    const capacity = selectedTrip.total_seats || selectedTrip.totalSeats || selectedTrip.vehicleType?.capacity || 30;
    const basePrice = selectedTrip.base_price || selectedTrip.price || selectedTrip.route?.basePrice || selectedTrip.route?.base_price || 25;
    const availableSeats = selectedTrip.available_seats || selectedTrip.availableSeats || capacity;

    const seats = [];
    const seatsPerRow = 4;
    const rows = Math.ceil(capacity / seatsPerRow);

    // Generate some random occupied seats for realism
    const occupiedCount = capacity - availableSeats;
    const occupiedSeats = new Set();

    while (occupiedSeats.size < occupiedCount) {
      const randomSeat = Math.floor(Math.random() * capacity) + 1;
      occupiedSeats.add(randomSeat);
    }

    for (let row = 1; row <= rows; row++) {
      for (const col of ['A', 'B', 'C', 'D']) {
        if (seats.length >= capacity) break;

        const seatNumber = `${row}${col}`;
        const seatIndex = seats.length + 1;
        const isOccupied = occupiedSeats.has(seatIndex);

        seats.push({
          id: `seat-${selectedTrip.id}-${seatNumber}`,
          seatNumber: seatNumber,
          isAvailable: !isOccupied,
          price: basePrice,
          seatType: {
            name: 'Standard',
            features: ['Air Conditioning', 'Reclining Seat']
          }
        });
      }
    }

    // Add seats to selectedTrip object
    selectedTrip.seats = seats;
  };

  if (!selectedTrip) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="w-8 h-8 border-4 border-primary-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading trip details...</p>
        </div>
      </div>
    );
  }

  const handleSeatSelect = (seat) => {
    if (!seat.isAvailable) return;

    if (selectedSeats.find(s => s.id === seat.id)) {
      // Deselect seat
      setSelectedSeats(prev => prev.filter(s => s.id !== seat.id));
    } else {
      // Select seat (limit to 4 seats)
      if (selectedSeats.length < 4) {
        setSelectedSeats(prev => [...prev, seat]);
      } else {
        addNotification({
          type: 'warning',
          message: 'Maximum 4 seats can be selected at once'
        });
      }
    }
  };

  const handleProceedToBooking = () => {
    if (selectedSeats.length === 0) {
      addNotification({
        type: 'warning',
        message: 'Please select at least one seat'
      });
      return;
    }

    if (!isAuthenticated) {
      navigate('/login');
      return;
    }

    // Store selected seats and proceed to booking
    navigate('/booking', { 
      state: { 
        trip: selectedTrip, 
        seats: selectedSeats,
        passengerInfo 
      } 
    });
  };

  const renderSeatLayout = () => {
    if (!selectedTrip.seats) return null;

    const rows = Math.ceil(selectedTrip.seats.length / 4);
    const seatRows = [];

    for (let row = 0; row < rows; row++) {
      const rowSeats = selectedTrip.seats.slice(row * 4, (row + 1) * 4);
      seatRows.push(rowSeats);
    }

    return (
      <div className="bg-white rounded-lg p-6 border border-gray-200">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Select Your Seats</h3>

        {/* Bus Layout */}
        <div className="max-w-md mx-auto">
          {/* Driver Area */}
          <div className="flex justify-end mb-4">
            <div className="w-12 h-8 bg-gray-300 rounded flex items-center justify-center text-xs text-gray-600">
              Driver
            </div>
          </div>

          {/* Seats */}
          <div className="space-y-2">
            {seatRows.map((rowSeats, rowIndex) => (
              <div key={rowIndex} className="flex justify-between items-center">
                {/* Left side seats (A, B) */}
                <div className="flex space-x-1">
                  {rowSeats.slice(0, 2).map((seat) => {
                    const isSelected = selectedSeats.find(s => s.id === seat.id);
                    return (
                      <button
                        key={seat.id}
                        onClick={() => handleSeatSelect(seat)}
                        className={`w-8 h-8 rounded text-xs font-medium transition-colors ${
                          !seat.isAvailable
                            ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                            : isSelected
                            ? 'bg-primary-600 text-white'
                            : 'bg-gray-100 text-gray-700 hover:bg-primary-100 border border-gray-300'
                        }`}
                        disabled={!seat.isAvailable}
                        title={`Seat ${seat.seatNumber} - RM ${seat.price}`}
                      >
                        {seat.seatNumber}
                      </button>
                    );
                  })}
                </div>

                {/* Aisle */}
                <div className="w-4"></div>

                {/* Right side seats (C, D) */}
                <div className="flex space-x-1">
                  {rowSeats.slice(2, 4).map((seat) => {
                    if (!seat) return <div key={`empty-${rowIndex}`} className="w-8 h-8"></div>;

                    const isSelected = selectedSeats.find(s => s.id === seat.id);
                    return (
                      <button
                        key={seat.id}
                        onClick={() => handleSeatSelect(seat)}
                        className={`w-8 h-8 rounded text-xs font-medium transition-colors ${
                          !seat.isAvailable
                            ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                            : isSelected
                            ? 'bg-primary-600 text-white'
                            : 'bg-gray-100 text-gray-700 hover:bg-primary-100 border border-gray-300'
                        }`}
                        disabled={!seat.isAvailable}
                        title={`Seat ${seat.seatNumber} - RM ${seat.price}`}
                      >
                        {seat.seatNumber}
                      </button>
                    );
                  })}
                </div>
              </div>
            ))}
          </div>

          {/* Legend */}
          <div className="mt-6 flex justify-center space-x-4 text-xs">
            <div className="flex items-center space-x-1">
              <div className="w-4 h-4 bg-gray-100 border border-gray-300 rounded"></div>
              <span>Available</span>
            </div>
            <div className="flex items-center space-x-1">
              <div className="w-4 h-4 bg-primary-600 rounded"></div>
              <span>Selected</span>
            </div>
            <div className="flex items-center space-x-1">
              <div className="w-4 h-4 bg-gray-300 rounded"></div>
              <span>Occupied</span>
            </div>
          </div>
        </div>
      </div>
    );
  };

  const totalPrice = selectedSeats.reduce((total, seat) => total + seat.price, 0);

  return (
    <div className="max-w-6xl mx-auto">
      {/* Back Button */}
      <div className="mb-6">
        <button
          onClick={() => navigate(-1)}
          className="flex items-center space-x-2 text-gray-600 hover:text-gray-900 transition-colors"
        >
          <ArrowLeft className="w-5 h-5" />
          <span>Back to search results</span>
        </button>
      </div>

      {/* Trip Information */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Trip Details */}
        <div className="lg:col-span-2 space-y-6">
          {/* Trip Header */}
          <div className="card">
            <div className="flex items-center justify-between mb-4">
              <h1 className="text-2xl font-bold text-gray-900">
                {selectedTrip.route?.name}
              </h1>
              <div className="text-right">
                <div className="text-2xl font-bold text-primary-600">
                  RM {selectedTrip.price || selectedTrip.route?.basePrice || selectedTrip.route?.base_price || 0}
                </div>
                <div className="text-sm text-gray-500">per person</div>
              </div>
            </div>

            <div className="flex items-center text-gray-600 mb-4">
              <span className="font-medium">{selectedTrip.originLocation?.city}</span>
              <ArrowRight className="w-5 h-5 mx-3 text-primary-500" />
              <span className="font-medium">{selectedTrip.destinationLocation?.city}</span>
            </div>

            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
              <div className="flex items-center space-x-2">
                <Clock className="w-4 h-4 text-primary-500" />
                <div>
                  <div className="font-medium text-gray-900">Departure</div>
                  <div>
                    {(() => {
                      try {
                        return selectedTrip.departureDatetime ? format(new Date(selectedTrip.departureDatetime), 'HH:mm') : 'N/A';
                      } catch (error) {
                        return 'Invalid time';
                      }
                    })()}
                  </div>
                  <div className="text-xs text-gray-500">
                    {(() => {
                      try {
                        return selectedTrip.departureDatetime ? format(new Date(selectedTrip.departureDatetime), 'MMM dd, yyyy') : 'N/A';
                      } catch (error) {
                        return 'Invalid date';
                      }
                    })()}
                  </div>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Clock className="w-4 h-4 text-primary-500" />
                <div>
                  <div className="font-medium text-gray-900">Arrival</div>
                  <div>
                    {(() => {
                      try {
                        const arrivalTime = selectedTrip.estimatedArrivalDatetime || selectedTrip.arrivalDatetime;
                        return arrivalTime ? format(new Date(arrivalTime), 'HH:mm') : 'N/A';
                      } catch (error) {
                        return 'Invalid time';
                      }
                    })()}
                  </div>
                  <div className="text-xs text-gray-500">
                    {(() => {
                      try {
                        const arrivalTime = selectedTrip.estimatedArrivalDatetime || selectedTrip.arrivalDatetime;
                        return arrivalTime ? format(new Date(arrivalTime), 'MMM dd, yyyy') : 'N/A';
                      } catch (error) {
                        return 'Invalid date';
                      }
                    })()}
                  </div>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Users className="w-4 h-4 text-primary-500" />
                <div>
                  <div className="font-medium text-gray-900">Available</div>
                  <div className="text-success-600">{selectedTrip.availableSeats} seats</div>
                </div>
              </div>

              <div className="flex items-center space-x-2">
                <Bus className="w-4 h-4 text-primary-500" />
                <div>
                  <div className="font-medium text-gray-900">Vehicle</div>
                  <div>{selectedTrip.vehicleType?.name}</div>
                </div>
              </div>
            </div>
          </div>

          {/* Seat Selection */}
          {renderSeatLayout()}

          {/* Trip Policies */}
          <div className="card">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Booking Policies</h3>
            <div className="space-y-3 text-sm text-gray-600">
              <div className="flex items-start space-x-2">
                <Info className="w-4 h-4 text-primary-500 mt-0.5" />
                <span>Free cancellation up to 24 hours before departure</span>
              </div>
              <div className="flex items-start space-x-2">
                <Info className="w-4 h-4 text-primary-500 mt-0.5" />
                <span>Please arrive at the terminal 15 minutes before departure</span>
              </div>
              <div className="flex items-start space-x-2">
                <Info className="w-4 h-4 text-primary-500 mt-0.5" />
                <span>Valid ID required for boarding</span>
              </div>
            </div>
          </div>
        </div>

        {/* Booking Summary */}
        <div className="lg:col-span-1">
          <div className="card sticky top-24">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Booking Summary</h3>

            {/* Selected Seats */}
            {selectedSeats.length > 0 && (
              <div className="mb-4">
                <h4 className="font-medium text-gray-900 mb-2">Selected Seats</h4>
                <div className="space-y-2">
                  {selectedSeats.map((seat) => (
                    <div key={seat.id} className="flex justify-between items-center text-sm">
                      <span>Seat {seat.seatNumber}</span>
                      <span className="font-medium">RM {seat.price}</span>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Total */}
            <div className="border-t border-gray-200 pt-4 mb-6">
              <div className="flex justify-between items-center">
                <span className="text-lg font-semibold text-gray-900">Total</span>
                <span className="text-2xl font-bold text-primary-600">
                  RM {totalPrice.toFixed(2)}
                </span>
              </div>
              <div className="text-sm text-gray-500 mt-1">
                {selectedSeats.length} seat{selectedSeats.length !== 1 ? 's' : ''} selected
              </div>
            </div>

            {/* Proceed Button */}
            <button
              onClick={handleProceedToBooking}
              disabled={selectedSeats.length === 0}
              className={`w-full py-3 px-4 rounded-lg font-medium transition-colors ${
                selectedSeats.length === 0
                  ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                  : 'btn-primary'
              }`}
            >
              {!isAuthenticated ? 'Sign In to Book' : 'Proceed to Booking'}
            </button>

            {/* Security Note */}
            <div className="mt-4 flex items-start space-x-2 text-xs text-gray-500">
              <CheckCircle className="w-4 h-4 text-success-500 mt-0.5" />
              <span>Your booking is secured with 256-bit SSL encryption</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TripDetails;
