import React, { useState, useEffect } from 'react';
import { 
  Bus, 
  Route, 
  Calendar,
  MapPin,
  Settings,
  BarChart3,
  Plus,
  Edit,
  Trash2,
  ArrowRight,
  TrendingUp,
  Users,
  Loader,
  AlertCircle,
  Truck,
  Map,
  X,
  PieChart,
  RefreshCw
} from 'lucide-react';
import { 
  getDashboardStats, 
  getAnalyticsData, 
  getVehicles, 
  getRoutes, 
  getTodaysSchedule,
  searchTrips,
  calculateOccupancy,
  handleApiError,
  getRouteAnalytics,
  getLocationsForRoutes,
  createRoute,
  getRouteStops,
  getSchedules,
  createSchedule,
  getTripDetails
} from '../../services/adminApi';
import { useApp } from '../../context/AppContext';
import RouteMap from '../../components/admin/RouteMap';
import RouteDetails from '../../components/admin/RouteDetails';
import { format, addDays } from 'date-fns';

const AdminDashboard = ({ activeTab = 'customer-service', setActiveTab }) => {
  const { locations, systemDate } = useApp();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [stats, setStats] = useState({
    activeRoutes: 0,
    activeTrips: 0,
    fleetSize: 0,
    scheduledTrips: 0
  });



  const [routes, setRoutes] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [todaysSchedule, setTodaysSchedule] = useState([]);
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [showRouteDetails, setShowRouteDetails] = useState(false);
  const [detailRoute, setDetailRoute] = useState(null);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    setLoading(true);
    try {
      // Load all dashboard data in parallel
      const [
        statsResponse,
        routesResponse,
        vehiclesResponse,
        scheduleResponse
      ] = await Promise.all([
        getDashboardStats(),
        getRoutes(),
        getVehicles(),
        getTodaysSchedule()
      ]);

      if (statsResponse.success) {
        setStats(statsResponse.data);
      }

      if (routesResponse.success) {
        setRoutes(routesResponse.data);
      }

      if (vehiclesResponse.success) {
        setVehicles(vehiclesResponse.data);
      }

      if (scheduleResponse.success) {
        setTodaysSchedule(scheduleResponse.data);
      }

      setError(null);
    } catch (err) {
      console.error('Error loading dashboard data:', err);
      setError(handleApiError(err));
    } finally {
      setLoading(false);
    }
  };

  const handleRouteClick = (route) => {
    setSelectedRoute(route);
  };

  const handleViewRoute = (route) => {
    setDetailRoute(route);
    setShowRouteDetails(true);
  };

  const clearSelection = () => {
    setSelectedRoute(null);
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex items-center justify-center h-64">
          <Loader className="w-8 h-8 animate-spin text-primary-600" />
          <span className="ml-2 text-gray-600">Loading dashboard data...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">Error loading dashboard</h3>
              <div className="mt-2 text-sm text-red-700">
                <p>{error}</p>
                <button 
                  onClick={loadDashboardData}
                  className="mt-2 text-red-800 underline hover:text-red-900"
                >
                  Try again
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Tab Content */}
      {activeTab === 'customer-service' && (
        <CustomerService />
      )}

      {activeTab === 'overview' && (
        <>
          {/* Action Buttons */}
          <div className="flex justify-end mb-6">
            <div className="flex space-x-3">
              <button 
                onClick={() => setActiveTab('analytics')}
                className="btn-secondary flex items-center"
              >
                <BarChart3 className="w-4 h-4 mr-2" />
                Analytics
              </button>
              <button 
                onClick={() => setActiveTab('schedule')}
                className="btn-primary flex items-center"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Route
              </button>
            </div>
          </div>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <div className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Active Routes</p>
                  <p className="text-3xl font-bold text-primary-600">{stats.activeRoutes}</p>
                </div>
                <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center">
                  <Route className="w-6 h-6 text-primary-600" />
                </div>
              </div>
            </div>

            <div className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Active Trips</p>
