        trips_scheduled: route.trips_scheduled,
        total_passengers: route.total_passengers,
        total_revenue: route.total_revenue,
        avg_occupancy: route.avg_occupancy,
        // undefined
        stops: [],
        seat_pricing: [],
        pricing_rules: []
