import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { format } from 'date-fns';
import { 
  Calendar, 
  MapPin, 
  Clock, 
  User, 
  CreditCard, 
  Settings, 
  Download,
  Eye,
  CheckCircle,
  AlertCircle,
  XCircle
} from 'lucide-react';
import { useApp } from '../context/AppContext';

const UserDashboard = () => {
  const { currentUser } = useApp();
  const [activeTab, setActiveTab] = useState('bookings');
  const [userBookings, setUserBookings] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    // Simulate loading user bookings
    const loadBookings = async () => {
      setIsLoading(true);
      // Simulate API delay
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Get user bookings (in real app, this would filter by user ID)
      const bookings = mockBookings.map(booking => ({
        ...booking,
        route: {
          name: 'KL - Johor Express',
          origin: 'Kuala Lumpur',
          destination: 'Johor Bahru'
        }
      }));

      setUserBookings(bookings);
      setIsLoading(false);
    };

    loadBookings();
  }, []);

  const getStatusColor = (status) => {
    switch (status) {
      case 'confirmed':
        return 'text-success-600 bg-success-100';
      case 'pending':
        return 'text-warning-600 bg-warning-100';
      case 'cancelled':
        return 'text-danger-600 bg-danger-100';
      default:
        return 'text-gray-600 bg-gray-100';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'confirmed':
        return <CheckCircle className="w-4 h-4" />;
      case 'pending':
        return <AlertCircle className="w-4 h-4" />;
      case 'cancelled':
        return <XCircle className="w-4 h-4" />;
      default:
        return <Clock className="w-4 h-4" />;
    }
  };

  const tabs = [
    { id: 'bookings', label: 'My Bookings', icon: Calendar },
    { id: 'profile', label: 'Profile', icon: User },
    { id: 'settings', label: 'Settings', icon: Settings }
  ];

  const renderBookings = () => {
    if (isLoading) {
      return (
        <div className="text-center py-12">
          <div className="w-8 h-8 border-4 border-primary-600 border-t-transparent rounded-full animate-spin mx-auto mb-4"></div>
          <p className="text-gray-600">Loading your bookings...</p>
        </div>
      );
    }

    if (userBookings.length === 0) {
      return (
        <div className="text-center py-12">
          <Calendar className="w-16 h-16 text-gray-300 mx-auto mb-4" />
          <h3 className="text-lg font-medium text-gray-900 mb-2">No bookings yet</h3>
          <p className="text-gray-600 mb-6">
            Start your journey by booking your first trip with us.
          </p>
          <Link to="/" className="btn-primary">
            Search Trips
          </Link>
        </div>
      );
    }

    return (
      <div className="space-y-4">
        {userBookings.map((booking) => (
          <div key={booking.id} className="card hover:shadow-lg transition-shadow duration-200">
            <div className="flex flex-col lg:flex-row lg:items-center justify-between">
              <div className="flex-1">
                <div className="flex items-center justify-between mb-3">
                  <div className="flex items-center space-x-3">
                    <h3 className="text-lg font-semibold text-gray-900">
                      {booking.route.name}
                    </h3>
                    <span className={`inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium ${getStatusColor(booking.bookingStatus)}`}>
                      {getStatusIcon(booking.bookingStatus)}
                      <span className="ml-1">{booking.bookingStatus.charAt(0).toUpperCase() + booking.bookingStatus.slice(1)}</span>
                    </span>
                  </div>
                  <div className="text-sm text-gray-500">
                    Ref: {booking.bookingReference}
                  </div>
                </div>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-4 text-sm">
                  <div className="flex items-center text-gray-600">
                    <MapPin className="w-4 h-4 mr-2 text-primary-500" />
                    <div>
                      <div className="font-medium text-gray-900">Route</div>
                      <div>{booking.route.origin} → {booking.route.destination}</div>
                    </div>
                  </div>

                  <div className="flex items-center text-gray-600">
                    <Calendar className="w-4 h-4 mr-2 text-primary-500" />
                    <div>
                      <div className="font-medium text-gray-900">Travel Date</div>
                      <div>{format(new Date(booking.travelDate), 'MMM dd, yyyy')}</div>
                    </div>
                  </div>

                  <div className="flex items-center text-gray-600">
                    <CreditCard className="w-4 h-4 mr-2 text-primary-500" />
                    <div>
                      <div className="font-medium text-gray-900">Amount</div>
                      <div className="text-primary-600 font-semibold">RM {booking.totalAmount}</div>
                    </div>
                  </div>
                </div>
              </div>

              <div className="mt-4 lg:mt-0 lg:ml-6 flex space-x-2">
                <button className="btn-outline flex items-center space-x-2 text-sm px-3 py-2">
                  <Eye className="w-4 h-4" />
                  <span>View Details</span>
                </button>
                <button className="btn-outline flex items-center space-x-2 text-sm px-3 py-2">
                  <Download className="w-4 h-4" />
                  <span>Download</span>
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  const renderProfile = () => (
    <div className="space-y-6">
      <div className="card">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Personal Information</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <label className="label">First Name</label>
            <div className="input bg-gray-50">{currentUser?.firstName || 'N/A'}</div>
          </div>
          <div>
            <label className="label">Last Name</label>
            <div className="input bg-gray-50">{currentUser?.lastName || 'N/A'}</div>
          </div>
          <div>
            <label className="label">Email Address</label>
            <div className="input bg-gray-50">{currentUser?.email || 'N/A'}</div>
          </div>
          <div>
            <label className="label">Phone Number</label>
            <div className="input bg-gray-50">{currentUser?.phone || 'Not provided'}</div>
          </div>
        </div>
        <div className="mt-6">
          <button className="btn-primary">Edit Profile</button>
        </div>
      </div>

      <div className="card">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Account Statistics</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="text-center">
            <div className="text-2xl font-bold text-primary-600">{userBookings.length}</div>
            <div className="text-sm text-gray-600">Total Bookings</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-success-600">
              {userBookings.filter(b => b.bookingStatus === 'confirmed').length}
            </div>
            <div className="text-sm text-gray-600">Confirmed Trips</div>
          </div>
          <div className="text-center">
            <div className="text-2xl font-bold text-warning-600">
              RM {userBookings.reduce((total, booking) => total + booking.totalAmount, 0).toFixed(2)}
            </div>
            <div className="text-sm text-gray-600">Total Spent</div>
          </div>
        </div>
      </div>
    </div>
  );

  const renderSettings = () => (
    <div className="space-y-6">
      <div className="card">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Notification Preferences</h3>
        <div className="space-y-4">
          <div className="flex items-center justify-between">
            <div>
              <div className="font-medium text-gray-900">Email Notifications</div>
              <div className="text-sm text-gray-600">Receive booking confirmations and updates</div>
            </div>
            <input type="checkbox" defaultChecked className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded" />
          </div>
          <div className="flex items-center justify-between">
            <div>
              <div className="font-medium text-gray-900">SMS Notifications</div>
              <div className="text-sm text-gray-600">Receive trip reminders and updates</div>
            </div>
            <input type="checkbox" defaultChecked className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded" />
          </div>
          <div className="flex items-center justify-between">
            <div>
              <div className="font-medium text-gray-900">Marketing Communications</div>
              <div className="text-sm text-gray-600">Receive promotional offers and news</div>
            </div>
            <input type="checkbox" className="h-4 w-4 text-primary-600 focus:ring-primary-500 border-gray-300 rounded" />
          </div>
        </div>
      </div>

      <div className="card">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">Security</h3>
        <div className="space-y-4">
          <button className="btn-outline w-full justify-center">
            Change Password
          </button>
          <button className="btn-outline w-full justify-center">
            Two-Factor Authentication
          </button>
          <button className="btn-outline w-full justify-center text-red-600 border-red-300 hover:bg-red-50">
            Delete Account
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <div className="max-w-6xl mx-auto">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">
          Welcome back, {currentUser?.firstName || 'User'}!
        </h1>
        <p className="text-gray-600 mt-1">Manage your bookings and account settings</p>
      </div>

      {/* Tabs */}
      <div className="mb-8">
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            {tabs.map((tab) => {
              const Icon = tab.icon;
              return (
                <button
                  key={tab.id}
                  onClick={() => setActiveTab(tab.id)}
                  className={`flex items-center space-x-2 py-2 px-1 border-b-2 font-medium text-sm transition-colors ${
                    activeTab === tab.id
                      ? 'border-primary-500 text-primary-600'
                      : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                  }`}
                >
                  <Icon className="w-4 h-4" />
                  <span>{tab.label}</span>
                </button>
              );
            })}
          </nav>
        </div>
      </div>

      {/* Tab Content */}
      <div>
        {activeTab === 'bookings' && renderBookings()}
        {activeTab === 'profile' && renderProfile()}
        {activeTab === 'settings' && renderSettings()}
      </div>
    </div>
  );
};

export default UserDashboard;
