import React from 'react';
import { format } from 'date-fns';
import { 
  MapPin, 
  Calendar, 
  Clock, 
  Users, 
  CreditCard,
  Bus,
  Phone,
  Mail,
  CheckCircle,
  ArrowRight,
  Printer,
  Download,
  X,
  AlertCircle
} from 'lucide-react';
import { useApp } from '../context/AppContext';

const BookingReceipt = ({ booking, trip, seats, passengerInfo, onClose, totalAmount }) => {
  const { systemDate } = useApp();
  const currentDate = new Date(systemDate);

  // Helper function to calculate total amount with fallback logic
  const calculateTotalAmount = () => {
    // Priority order for total amount calculation
    if (totalAmount && totalAmount > 0) return totalAmount;
    if (booking?.totalAmount && booking.totalAmount > 0) return booking.totalAmount;
    if (booking?.total_amount && booking.total_amount > 0) return booking.total_amount;
    if (trip?.basePrice && trip.basePrice > 0) return trip.basePrice;
    if (trip?.base_price && trip.base_price > 0) return trip.base_price;
    if (trip?.price && trip.price > 0) return trip.price;

    // Calculate from seats if available
    if (seats && seats.length > 0) {
      const seatsTotal = seats.reduce((total, seat) => {
        const seatPrice = seat.price || seat.amount || seat.cost || 0;
        return total + seatPrice;
      }, 0);
      if (seatsTotal > 0) return seatsTotal;
    }

    // Ultimate fallback
    return 45.00;
  };

  // Helper function to format price consistently
  const formatPrice = (amount) => {
    const price = parseFloat(amount || 0);
    return price.toFixed(2);
  };

  // Get the calculated total amount
  const calculatedTotal = calculateTotalAmount();

  const handlePrint = () => {
    window.print();
  };

  const handleDownload = () => {
    // Create a new window for PDF generation with enhanced styling
    const printWindow = window.open('', '_blank');
    printWindow.document.write(`
      <!DOCTYPE html>
      <html lang="en">
        <head>
          <meta charset="UTF-8">
          <meta name="viewport" content="width=device-width, initial-scale=1.0">
          <title>Booking Receipt - ${booking.bookingReference}</title>
          <style>
            * {
              margin: 0;
              padding: 0;
              box-sizing: border-box;
            }

            body {
              font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
              line-height: 1.6;
              color: #1f2937;
              background: #ffffff;
              font-size: 14px;
            }

            .receipt-container {
              max-width: 210mm;
              margin: 0 auto;
              padding: 15mm;
              background: white;
              box-shadow: 0 0 20px rgba(0,0,0,0.1);
            }

            .header {
              text-align: center;
              border-bottom: 3px solid #2563eb;
              padding-bottom: 20px;
              margin-bottom: 30px;
              background: linear-gradient(135deg, #f8fafc 0%, #e2e8f0 100%);
              padding: 25px;
              border-radius: 12px;
              margin-bottom: 25px;
            }

            .company-logo {
              font-size: 32px;
              font-weight: 800;
              color: #2563eb;
              margin-bottom: 8px;
              text-transform: uppercase;
              letter-spacing: 2px;
            }

            .receipt-title {
              font-size: 28px;
              font-weight: 700;
              margin-bottom: 8px;
              color: #1f2937;
              letter-spacing: 1px;
            }

            .receipt-subtitle {
              color: #6b7280;
              font-size: 16px;
              font-weight: 500;
            }

            .booking-reference {
              background: #2563eb;
              color: white;
              padding: 12px 24px;
              border-radius: 25px;
              font-size: 18px;
              font-weight: 700;
              margin-top: 20px;
              display: inline-block;
              letter-spacing: 1px;
            }

            .section {
              margin-bottom: 25px;
              background: #ffffff;
              border-radius: 12px;
              overflow: hidden;
            }

            .section-header {
              background: linear-gradient(135deg, #2563eb 0%, #1d4ed8 100%);
              color: white;
              padding: 15px 20px;
              font-size: 18px;
              font-weight: 600;
              display: flex;
              align-items: center;
            }

            .section-header svg {
              margin-right: 10px;
            }

            .section-content {
              padding: 20px;
              background: #f9fafb;
              border: 1px solid #e5e7eb;
              border-top: none;
            }

            .route-visual {
              background: white;
              padding: 25px;
              border-radius: 12px;
              margin-bottom: 20px;
              box-shadow: 0 2px 8px rgba(0,0,0,0.1);
            }

            .route-cities {
              display: flex;
              align-items: center;
              justify-content: space-between;
              margin-bottom: 25px;
            }

            .city {
              text-align: center;
              flex: 1;
              padding: 0 15px;
            }

            .city-name {
              font-size: 24px;
              font-weight: 700;
              color: #1f2937;
              margin-bottom: 8px;
            }

            .terminal-name {
              font-size: 16px;
              color: #4b5563;
              font-weight: 600;
              margin-bottom: 4px;
            }

            .terminal-address {
              font-size: 12px;
              color: #6b7280;
              margin-bottom: 12px;
            }

            .departure-time {
              font-size: 20px;
              font-weight: 700;
              color: #2563eb;
              background: #eff6ff;
              padding: 8px 16px;
              border-radius: 20px;
              display: inline-block;
            }

            .route-arrow {
              font-size: 32px;
              color: #2563eb;
              font-weight: bold;
              margin: 0 20px;
            }

            .total-amount {
              background: linear-gradient(135deg, #2563eb 0%, #1d4ed8 100%);
              color: white;
              text-align: center;
              padding: 20px;
              border-radius: 12px;
              margin-top: 20px;
            }

            .amount-value {
              font-size: 32px;
              font-weight: 800;
              margin-bottom: 5px;
            }

            .amount-label {
              font-size: 14px;
              opacity: 0.9;
              text-transform: uppercase;
              letter-spacing: 1px;
            }

            .info-grid {
              display: grid;
              grid-template-columns: 1fr 1fr;
              gap: 30px;
            }

            .info-item {
              display: flex;
              justify-content: space-between;
              align-items: center;
              padding: 10px 0;
              border-bottom: 1px solid #e5e7eb;
            }

            .info-item:last-child {
              border-bottom: none;
            }

            .info-label {
              font-weight: 600;
              color: #4b5563;
              flex: 1;
            }

            .info-value {
              font-weight: 700;
              color: #1f2937;
              text-align: right;
              flex: 1;
            }

            .seats-grid {
              display: flex;
              flex-wrap: wrap;
              gap: 8px;
              margin-top: 15px;
            }

            .seat-item {
              background: linear-gradient(135deg, #2563eb 0%, #1d4ed8 100%);
              color: white;
              padding: 8px 16px;
              border-radius: 20px;
              font-size: 14px;
              font-weight: 600;
              box-shadow: 0 2px 4px rgba(37, 99, 235, 0.3);
            }

            .status-badge {
              display: inline-flex;
