import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { AppProvider, useApp } from './context/AppContext';
import './styles/print.css';
import Navbar from './components/layout/Navbar';
import NotificationCenter from './components/NotificationCenter';
import HomePage from './pages/HomePage';
import SearchResults from './pages/SearchResults';
import TripDetails from './pages/TripDetails';
import Login from './pages/Login';
import Register from './pages/Register';
import UserDashboard from './pages/UserDashboard';
import ProfilePage from './pages/ProfilePage';
import AdminDashboard from './pages/admin/AdminDashboard';
import Booking from './pages/Booking';
import BookingConfirmation from './pages/BookingConfirmation';
import ProtectedRoute from './components/ProtectedRoute';
import AdminPopularTrips from './pages/admin/AdminPopularTrips';
import AdminTrips from './pages/admin/AdminTrips';
import AdminVehicles from './pages/admin/AdminVehicles';
import AdminBookings from './pages/admin/AdminBookings';
import AdminRoutes from './pages/admin/AdminRoutes';

// Admin wrapper component to manage admin state
const AdminWrapper = ({ defaultActiveTab = 'customer-service' }) => {
  const [activeTab, setActiveTab] = useState(defaultActiveTab);

  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar adminActiveTab={activeTab} setAdminActiveTab={setActiveTab} />
      <AdminDashboard activeTab={activeTab} setActiveTab={setActiveTab} />
    </div>
  );
};

// Regular layout wrapper for non-admin pages
const RegularLayout = ({ children, isHomePage = false }) => {
  return (
    <div className="min-h-screen bg-gray-50">
      <Navbar />
      {isHomePage ? (
        <main>
          {children}
        </main>
      ) : (
        <main className="pt-20 pb-12 px-4 sm:px-6 lg:px-8">
          {children}
        </main>
      )}
    </div>
  );
};

// Home route wrapper that redirects admin users
const HomeWrapper = () => {
  const { currentUser, isAuthenticated } = useApp();

  // Only redirect if user is authenticated and is admin
  if (isAuthenticated && currentUser?.role === 'admin') {
    console.log('Redirecting admin user to /admin');
    return <Navigate to="/admin" replace />;
  }

  console.log('Showing homepage for normal user or guest');
  return <HomePage />;
};

function AppContent() {
  return (
    <Router>
      <NotificationCenter />

      <Routes>
        {/* Home route with admin redirect */}
        <Route path="/" element={
          <RegularLayout isHomePage={true}>
            <HomeWrapper />
          </RegularLayout>
        } />

        {/* Regular customer pages */}
        <Route path="/search" element={
          <RegularLayout>
            <SearchResults />
          </RegularLayout>
        } />
        <Route path="/trip/:tripId" element={
          <RegularLayout>
            <TripDetails />
          </RegularLayout>
        } />
        <Route path="/login" element={
          <RegularLayout>
            <Login />
          </RegularLayout>
        } />
        <Route path="/register" element={
          <RegularLayout>
            <Register />
          </RegularLayout>
        } />

        {/* Protected customer routes */}
        <Route path="/dashboard" element={
          <RegularLayout>
            <ProtectedRoute>
              <UserDashboard />
            </ProtectedRoute>
          </RegularLayout>
        } />

        <Route path="/booking" element={
          <RegularLayout>
            <ProtectedRoute>
              <Booking />
            </ProtectedRoute>
          </RegularLayout>
        } />

        <Route path="/booking-confirmation" element={
          <RegularLayout>
            <ProtectedRoute>
              <BookingConfirmation />
            </ProtectedRoute>
          </RegularLayout>
        } />

        <Route path="/bookings" element={
          <RegularLayout>
            <ProtectedRoute>
              <UserDashboard />
            </ProtectedRoute>
          </RegularLayout>
