// Schedule Management Component
const ScheduleManagement = ({ todaysSchedule = [], routes = [], vehicles = [], onRefresh }) => {
  const [selectedDate, setSelectedDate] = useState('2025-06-24');
  const [filteredSchedule, setFilteredSchedule] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [createForm, setCreateForm] = useState({
    route_id: '',
    vehicle_id: '',
    departure_date: '2025-06-24',
    departure_time: '08:00',
    base_price: 0
  });

  // Filter schedule by selected date
  useEffect(() => {
    const loadScheduleForDate = async () => {
      try {
        const response = await fetch(`http://localhost:5001/api/admin/schedules?startDate=${selectedDate}&endDate=${selectedDate}`);
        if (response.ok) {
          const data = await response.json();
          if (data.success) {
            setFilteredSchedule(data.data);
          }
        }
      } catch (error) {
        console.error('Error loading schedule for date:', error);
        // API
        setFilteredSchedule([]);
      }
    };

    loadScheduleForDate();
  }, [selectedDate]);

  const formatTime = (datetime) => {
    try {
      return new Date(datetime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
    } catch (error) {
      return 'Invalid time';
    }
  };

  const formatDate = (datetime) => {
    try {
      return new Date(datetime).toLocaleDateString('en-GB');
    } catch (error) {
      return 'Invalid date';
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'scheduled': return 'bg-blue-100 text-blue-800';
      case 'boarding': return 'bg-yellow-100 text-yellow-800';
      case 'in_transit': return 'bg-green-100 text-green-800';
      case 'arrived': return 'bg-gray-100 text-gray-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      case 'delayed': return 'bg-orange-100 text-orange-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const handleCreateTrip = async () => {
    try {
      const departureDateTime = `${createForm.departure_date} ${createForm.departure_time}:00`;
      // Estimate arrival time (add 4.5 hours for now)
      const arrivalDateTime = new Date(new Date(departureDateTime).getTime() + 4.5 * 60 * 60 * 1000)
        .toISOString().slice(0, 19).replace('T', ' ');

      const response = await fetch('http://localhost:5001/api/admin/schedules', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          route_id: createForm.route_id,
          vehicle_id: createForm.vehicle_id,
          departure_datetime: departureDateTime,
          estimated_arrival_datetime: arrivalDateTime,
          base_price: parseFloat(createForm.base_price)
        }),
      });

      if (response.ok) {
        setShowCreateModal(false);
        setCreateForm({
          route_id: '',
          vehicle_id: '',
          departure_date: '2025-06-24',
          departure_time: '08:00',
          base_price: 0
        });
        onRefresh();
        alert('Trip scheduled successfully!');
      } else {
        alert('Failed to schedule trip');
      }
    } catch (error) {
      console.error('Error creating trip:', error);
      alert('Error scheduling trip');
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Schedule Management</h2>
        <div className="flex space-x-3">
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            className="px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
            min="2025-06-24"
            max="2025-07-31"
          />
          <button 
            className="btn-primary flex items-center"
            onClick={() => setShowCreateModal(true)}
          >
            <Calendar className="w-4 h-4 mr-2" />
            Create Schedule
          </button>
        </div>
      </div>

      <div className="card">
        <h3 className="text-lg font-semibold mb-4">
          Schedule for {formatDate(selectedDate)}
          <span className="ml-2 text-sm font-normal text-gray-600">
            ({filteredSchedule.length} trips)
          </span>
        </h3>

        {filteredSchedule.length === 0 ? (
          <div className="text-center py-8 text-gray-500">
            <Calendar className="w-12 h-12 mx-auto mb-3 text-gray-300" />
            <p>No trips scheduled for this date</p>
          </div>
        ) : (
          <div className="space-y-3">
            {filteredSchedule.map((trip) => {
              const occupiedSeats = trip.occupied_seats || (trip.total_seats - trip.available_seats);
              const occupancyPercentage = trip.occupancy_percentage || 
                Math.round((occupiedSeats / trip.total_seats) * 100);

              return (
                <div key={trip.id} className="p-4 border border-gray-200 rounded-lg hover:bg-gray-50">
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-6">
                        {/* Route Info */}
                        <div className="min-w-0 flex-1">
                          <div className="font-medium text-gray-900 mb-1">
                            {trip.route_name}
                          </div>
                          <div className="text-sm text-gray-600">
                            {trip.origin_name}  {trip.destination_name}
                          </div>
                        </div>

                        {/* Vehicle Info */}
                        <div className="text-sm text-gray-600">
                          <div className="font-medium">{trip.vehicle_number}</div>
                          <div>{trip.vehicle_model}</div>
                        </div>

                        {/* Time Info */}
                        <div className="text-sm text-gray-600">
                          <div className="font-medium">
                            {formatTime(trip.departure_datetime)}
                          </div>
                          <div>
                            to {formatTime(trip.estimated_arrival_datetime)}
                          </div>
                        </div>

                        {/* Status */}
                        <div>
                          <span className={`px-2 py-1 rounded-full text-xs font-medium ${getStatusColor(trip.status)}`}>
                            {trip.status.replace('_', ' ').toUpperCase()}
                          </span>
                        </div>
                      </div>
                    </div>

                    {/* Occupancy Info */}
                    <div className="flex items-center space-x-4 ml-4">
                      <div className="text-sm text-right">
                        <div className="font-medium">
                          {occupiedSeats}/{trip.total_seats} seats
                        </div>
                        <div className={`text-xs px-2 py-1 rounded-full ${
                          occupancyPercentage > 80 ? 'bg-red-100 text-red-600' :
                          occupancyPercentage > 60 ? 'bg-yellow-100 text-yellow-600' :
                          'bg-green-100 text-green-600'
                        }`}>
                          {occupancyPercentage}% occupied
                        </div>
                      </div>
                      <div className="w-16 bg-gray-200 rounded-full h-2">
                        <div 
                          className="h-2 rounded-full transition-all"
                          style={{ 
                            width: `${Math.min(occupancyPercentage, 100)}%`,
                            backgroundColor: occupancyPercentage > 80 ? '#EF4444' : 
                              occupancyPercentage > 60 ? '#F59E0B' : '#10B981'
                          }}
                        ></div>
                      </div>

                      {/* Action Buttons */}
                      <div className="flex space-x-2">
                        <button className="p-2 text-gray-600 hover:text-blue-600 hover:bg-blue-50 rounded">
                          <Edit className="w-4 h-4" />
                        </button>
                        <button className="p-2 text-gray-600 hover:text-red-600 hover:bg-red-50 rounded">
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
                 )}
      </div>

      {/* Create Schedule Modal */}
      {showCreateModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 w-full max-w-md">
            <h3 className="text-lg font-semibold mb-4">Create New Trip Schedule</h3>

            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Route
                </label>
                <select
                  value={createForm.route_id}
                  onChange={(e) => setCreateForm(prev => ({ ...prev, route_id: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                >
                  <option value="">Select a route</option>
                  {routes.map(route => (
                    <option key={route.id} value={route.id}>
                      {route.name || `${route.origin_name}  ${route.destination_name}`}
                    </option>
                  ))}
                </select>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Vehicle
                </label>
                <select
                  value={createForm.vehicle_id}
                  onChange={(e) => setCreateForm(prev => ({ ...prev, vehicle_id: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                >
                  <option value="">Select a vehicle</option>
                  {vehicles.filter(v => v.status === 'active').map(vehicle => (
                    <option key={vehicle.id} value={vehicle.id}>
                      {vehicle.license_plate || vehicle.vehicle_number} - {vehicle.model}
                    </option>
                  ))}
                </select>
              </div>

              <div className="grid grid-cols-2 gap-3">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Departure Date
                  </label>
                  <input
                    type="date"
                    value={createForm.departure_date}
                    onChange={(e) => setCreateForm(prev => ({ ...prev, departure_date: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    min="2025-06-24"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Departure Time
                  </label>
                  <input
                    type="time"
                    value={createForm.departure_time}
                    onChange={(e) => setCreateForm(prev => ({ ...prev, departure_time: e.target.value }))}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Base Price (RM)
                </label>
                <input
                  type="number"
                  step="0.50"
                  min="0"
                  value={createForm.base_price}
                  onChange={(e) => setCreateForm(prev => ({ ...prev, base_price: e.target.value }))}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="e.g. 45.00"
                />
              </div>
            </div>

            <div className="flex space-x-3 mt-6">
              <button 
                className="btn-primary flex-1"
                onClick={handleCreateTrip}
                disabled={!createForm.route_id || !createForm.vehicle_id || !createForm.base_price}
              >
                Schedule Trip
              </button>
              <button 
                className="btn-secondary flex-1"
                onClick={() => setShowCreateModal(false)}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

// Analytics Component
const Analytics = ({ allTrips, routes }) => {
  const totalRevenue = allTrips.reduce((sum, trip) => {
    const route = routes.find(r => r.id === trip.routeId);
    return sum + (route?.basePrice || 0) * calculateOccupancy(trip);
  }, 0);

  const avgOccupancy = allTrips.length > 0 
    ? allTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0) / allTrips.length
    : 0;

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold text-gray-900">Analytics & Reports</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Total Revenue</p>
              <p className="text-2xl font-bold text-green-600">RM {totalRevenue.toLocaleString()}</p>
            </div>
            <TrendingUp className="w-8 h-8 text-green-600" />
          </div>
        </div>

        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Average Occupancy</p>
              <p className="text-2xl font-bold text-blue-600">{avgOccupancy.toFixed(1)}%</p>
            </div>
            <Users className="w-8 h-8 text-blue-600" />
          </div>
        </div>

        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Total Passengers</p>
              <p className="text-2xl font-bold text-purple-600">{allTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0)}</p>
            </div>
            <Users className="w-8 h-8 text-purple-600" />
          </div>
        </div>
      </div>

      <div className="card">
        <h3 className="text-lg font-semibold mb-4">Route Performance</h3>
        <div className="space-y-4">
          {routes.map((route) => {
            const routeTrips = allTrips.filter(trip => trip.routeId === route.id);
            const routeRevenue = routeTrips.reduce((sum, trip) => sum + route.basePrice * calculateOccupancy(trip), 0);
            const routeOccupancy = routeTrips.length > 0 
              ? routeTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0) / routeTrips.length
              : 0;

            return (
              <div key={route.id} className="p-4 border border-gray-200 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <h4 className="font-medium text-gray-900">{route.name}</h4>
                    <p className="text-sm text-gray-600">{routeTrips.length} trips scheduled</p>
                  </div>
                  <div className="text-right space-y-1">
                    <div className="text-sm font-medium text-green-600">RM {routeRevenue.toLocaleString()}</div>
                    <div className="flex items-center space-x-2">
                      <div className="w-20 bg-gray-200 rounded-full h-2">
                        <div 
                          className="h-2 rounded-full transition-all"
                          style={{ 
                            width: `${Math.min(routeOccupancy, 100)}%`,
                            backgroundColor: routeOccupancy > 80 ? '#EF4444' : routeOccupancy > 60 ? '#F59E0B' : '#10B981'
                          }}
                        ></div>
                      </div>
                      <span className="text-xs font-medium">{routeOccupancy.toFixed(0)}%</span>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

