      {/* Route Details Modal */}
      {showRouteDetails && (
        <div style={{ zIndex: 9999 }}>
          <RouteDetails
            route={detailRoute}
            locations={locations}
            onClose={() => {
              setShowRouteDetails(false);
              setDetailRoute(null);
            }}
          />
        </div>
      )}
    </div>
  );
};

