                                {isDestination && ' (Destination)'}
                              </span>

                              {/* Time offset */}
                              {!isOrigin && (
                                <span className="ml-auto text-xs text-gray-500">
                                  +{Math.floor(stop.arrival_offset_minutes / 60)} hrs 
                                  {stop.arrival_offset_minutes % 60 > 0 ? ` ${stop.arrival_offset_minutes % 60} mins` : ''}
