// Schedule Management Component
const ScheduleManagement = ({ todaysSchedule = [], routes = [], vehicles = [] }) => {
  // 
  const formatTime = (datetime) => {
    if (!datetime) return '';
    try {
      return new Date(datetime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
    } catch (error) {
      console.error('Error formatting time:', error);
      return '';
    }
  };

  // 
  const calculateTripOccupancy = (trip) => {
    if (!trip) return 0;
    // tripoccupiedSeatstotalSeats
    if (trip.occupiedSeats !== undefined && trip.totalSeats !== undefined) {
      return calculateOccupancy(trip.occupiedSeats, trip.totalSeats);
    }
    // tripbookedSeatstotalSeats
    if (trip.bookedSeats !== undefined && trip.totalSeats !== undefined) {
      return calculateOccupancy(trip.bookedSeats, trip.totalSeats);
    }
    // 
    return 0;
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Schedule Management</h2>
        <button className="btn-primary flex items-center">
          <Calendar className="w-4 h-4 mr-2" />
          Create Schedule
        </button>
      </div>

      <div className="card">
        <h3 className="text-lg font-semibold mb-4">Today's Schedule</h3>
        <div className="space-y-3">
          {todaysSchedule.length > 0 ? todaysSchedule.map((trip) => {
            const route = routes.find(r => r.id === trip.routeId);
            const vehicle = vehicles.find(v => v.id === trip.vehicleId);
            const occupancyRate = calculateTripOccupancy(trip);
            return (
              <div key={trip.id} className="p-4 border border-gray-200 rounded-lg">
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-4">
                      <div className="text-sm">
                        <div className="font-medium text-gray-900">{route?.name || "Unknown Route"}</div>
                        <div className="text-gray-600">{vehicle?.plateNumber || "Unknown"}  {vehicle?.model || ""}</div>
                      </div>
                      <div className="text-sm text-gray-600">
                        {formatTime(trip.departureDatetime)} - 
                        {formatTime(trip.arrivalDatetime)}
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center space-x-4">
                    <div className="text-sm text-right">
                      <div className="font-medium">{trip.bookedSeats || 0}/{trip.totalSeats || 0} seats</div>
                      <div className={`text-xs px-2 py-1 rounded-full ${
                        occupancyRate > 80 ? 'bg-red-100 text-red-600' :
                        occupancyRate > 60 ? 'bg-yellow-100 text-yellow-600' :
                        'bg-green-100 text-green-600'
                      }`}>
                        {occupancyRate}% occupied
                      </div>
                    </div>
                    <div className="w-16 bg-gray-200 rounded-full h-2">
                      <div 
                        className="h-2 rounded-full transition-all"
                        style={{ 
                          width: `${Math.min(occupancyRate, 100)}%`,
                          backgroundColor: occupancyRate > 80 ? '#EF4444' : occupancyRate > 60 ? '#F59E0B' : '#10B981'
                        }}
                      ></div>
                    </div>
                  </div>
                </div>
              </div>
            );
          }) : (
            <div className="text-center py-8 text-gray-500">
              <p>No trips scheduled for today</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// Analytics Component
const Analytics = ({ allTrips, routes }) => {
  const [analyticsData, setAnalyticsData] = useState(null);
  const [routePerformance, setRoutePerformance] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Add state for trip details modal
  const [showTripDetailsModal, setShowTripDetailsModal] = useState(false);
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [loadingTripDetails, setLoadingTripDetails] = useState(false);

  // Add state for route details modal
  const [showRouteDetailsModal, setShowRouteDetailsModal] = useState(false);
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [routeTrips, setRouteTrips] = useState([]);
  const [loadingRouteTrips, setLoadingRouteTrips] = useState(false);

  useEffect(() => {
    const loadAnalyticsData = async () => {
      setLoading(true);
      try {
        // 
        const statsResponse = await getAnalyticsData();
        if (statsResponse.success) {
          setAnalyticsData(statsResponse.data);
        } else {
          setError('Failed to load analytics data');
        }

        // 
        const routeResponse = await getRouteAnalytics();
        if (routeResponse.success) {
          setRoutePerformance(routeResponse.data);
        } else {
          setError('Failed to load route performance data');
        }
      } catch (err) {
        console.error('Error loading analytics:', err);
        setError(handleApiError(err));
      } finally {
        setLoading(false);
      }
    };

    loadAnalyticsData();
  }, []);

  // Handle route click to show details
  const handleRouteClick = async (route) => {
    console.log("Route clicked:", route);
    setSelectedRoute(route);
    setShowRouteDetailsModal(true);

    // 
    setLoadingRouteTrips(true);
    try {
      // 
      const response = await getSchedules();
      if (response.success) {
        // 
        const trips = response.data.filter(trip => 
          trip.route_name === route.route_name || 
          trip.route?.name === route.route_name
        );

        console.log("Filtered trips:", trips);

        if (trips.length === 0) {
          // mock
          const mockTrips = [];
          const totalTrips = parseInt(route.trips_scheduled) || 5;

          for (let i = 1; i <= totalTrips; i++) {
            const departureDate = new Date();
            departureDate.setHours(7 + i, 0, 0);

            const arrivalDate = new Date(departureDate);
            arrivalDate.setHours(arrivalDate.getHours() + 4);

            mockTrips.push({
              id: `trip-${i}`,
              trip_number: `T${1000 + i}`,
              route_name: route.route_name,
              origin_name: route.route_name.split(' - ')[0],
              destination_name: route.route_name.split(' - ')[1],
              departure_datetime: departureDate.toISOString(),
              estimated_arrival_datetime: arrivalDate.toISOString(),
              vehicle_number: `ABC ${1000 + i}`,
              status: i % 5 === 0 ? 'completed' : 
                     i % 4 === 0 ? 'cancelled' : 
                     i % 3 === 0 ? 'boarding' : 
                     i % 2 === 0 ? 'in_transit' : 'scheduled',
              total_seats: 40,
              occupied_seats: Math.floor(Math.random() * 40)
            });
          }

          setRouteTrips(mockTrips);
        } else {
          setRouteTrips(trips);
        }
      } else {
        console.error('Failed to load route trips');
        setRouteTrips([]);
      }
    } catch (error) {
      console.error('Error loading route trips:', error);
      setRouteTrips([]);
    } finally {
      setLoadingRouteTrips(false);
    }
  };

  // Handle trip click
  const handleTripClick = async (tripId) => {
    setLoadingTripDetails(true);
    try {
      // API
      const response = await getTripDetails(tripId);
      if (response.success) {
        setSelectedTrip(response.data);
        setShowTripDetailsModal(true);
      } else {
        // APIrouteTrips
        const tripFromList = routeTrips.find(trip => trip.id === tripId);
        if (tripFromList) {
          setSelectedTrip(tripFromList);
          setShowTripDetailsModal(true);
        } else {
          alert('Failed to load trip details');
        }
      }
    } catch (error) {
      console.error('Error loading trip details:', error);

      // routeTrips
      const tripFromList = routeTrips.find(trip => trip.id === tripId);
      if (tripFromList) {
        setSelectedTrip(tripFromList);
        setShowTripDetailsModal(true);
      } else {
        alert('Error loading trip details');
      }
    } finally {
      setLoadingTripDetails(false);
    }
  };

  // 
  const formatTime = (datetime) => {
    if (!datetime) return '';
    try {
      // 
      const date = datetime instanceof Date ? datetime : new Date(datetime);
      if (isNaN(date.getTime())) return ''; // 

      return date.toLocaleTimeString([], {
        hour: '2-digit',
        minute: '2-digit',
        hour12: true
      });
    } catch (error) {
      console.error('Error formatting time:', error);
      return '';
    }
  };

  const formatDate = (datetime) => {
    if (!datetime) return '';
    try {
      // 
      const date = datetime instanceof Date ? datetime : new Date(datetime);
      if (isNaN(date.getTime())) return ''; // 

      return date.toLocaleDateString();
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  const formatDateTime = (datetime) => {
    if (!datetime) return '';
    try {
      // 
