// Customer Service Component for Walk-in Ticket Sales
const CustomerService = () => {
  const { locations } = useApp();
  const [currentStep, setCurrentStep] = useState(1);
  const [searchResults, setSearchResults] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [customerInfo, setCustomerInfo] = useState({
    fullName: '',
    phone: '',
    email: '',
    icNumber: '',
    passengerCount: 1
  });
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState(null);

  const [searchForm, setSearchForm] = useState({
    origin: '',
    destination: '',
    date: format(new Date(), 'yyyy-MM-dd'),
    isRoundTrip: false,
    returnDate: format(addDays(new Date(), 1), 'yyyy-MM-dd')
  });

  // 
  const formatTime = (datetime) => {
    if (!datetime) return '';
    try {
      return new Date(datetime).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});
    } catch (error) {
      console.error('Error formatting time:', error);
      return '';
    }
  };

  const formatDate = (datetime) => {
    if (!datetime) return '';
    try {
      return new Date(datetime).toLocaleDateString();
    } catch (error) {
      console.error('Error formatting date:', error);
      return '';
    }
  };

  const formatDateTime = (datetime) => {
    if (!datetime) return '';
    try {
      const date = new Date(datetime);
      return `${date.toLocaleDateString()} at ${date.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}`;
    } catch (error) {
      console.error('Error formatting date time:', error);
      return '';
    }
  };

  // 
  const calculateTripOccupancy = (trip) => {
    if (!trip) return 0;

    // 
    if (trip.availableSeats !== undefined && trip.totalSeats !== undefined) {
      const occupiedSeats = trip.totalSeats - trip.availableSeats;
      return calculateOccupancy(occupiedSeats, trip.totalSeats);
    }

    // tripoccupiedSeatstotalSeats
    if (trip.occupiedSeats !== undefined && trip.totalSeats !== undefined) {
      return calculateOccupancy(trip.occupiedSeats, trip.totalSeats);
    }

    // tripbookedSeatstotalSeats
    if (trip.bookedSeats !== undefined && trip.totalSeats !== undefined) {
      return calculateOccupancy(trip.bookedSeats, trip.totalSeats);
    }

    // 
    return 0;
  };

  const handleSearch = async () => {
    setIsProcessing(true);

    try {
      const searchParams = {
        origin: searchForm.origin,
        destination: searchForm.destination,
        departureDate: searchForm.date,
        passengers: customerInfo.passengerCount
      };

      const response = await searchTrips(searchParams);

      if (response.success) {
        setSearchResults(response.data);
        setCurrentStep(2);
      } else {
        setError('Failed to search trips. Please try again.');
      }
    } catch (err) {
      console.error('Search error:', err);
      setError(handleApiError(err));
    } finally {
      setIsProcessing(false);
    }
  };

  const handleTripSelect = (trip) => {
    setSelectedTrip(trip);
    setCurrentStep(3);
  };

  const handleCustomerSubmit = () => {
    if (customerInfo.fullName && customerInfo.phone) {
      setCurrentStep(4);
    }
  };

  const handlePaymentComplete = () => {
    // Process booking
    const bookingRef = `TBS${format(new Date(), 'yyyyMMddHHmm')}`;
    alert(`Booking confirmed! Reference: ${bookingRef}`);

    // Reset form
    setCurrentStep(1);
    setSearchResults([]);
    setSelectedTrip(null);
    setCustomerInfo({ fullName: '', phone: '', email: '', icNumber: '', passengerCount: 1 });
  };

  const resetSearch = () => {
    setCurrentStep(1);
    setSearchResults([]);
    setSelectedTrip(null);
  };

  return (
    <div className="space-y-6">
      {/* Error Message */}
      {error && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <p className="text-sm text-red-700">{error}</p>
              <button 
                onClick={() => setError(null)}
                className="mt-2 text-red-800 underline hover:text-red-900 text-sm"
              >
                Dismiss
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-gray-900">Customer Service Portal</h2>
          <p className="text-gray-600">Help walk-in customers purchase tickets quickly and efficiently</p>
        </div>
        {currentStep > 1 && (
          <button onClick={resetSearch} className="btn-secondary">
            New Search
          </button>
        )}
      </div>

      {/* Progress Steps */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-4">
        <div className="flex items-center justify-between">
          {['Search Trips', 'Select Trip', 'Customer Info', 'Payment'].map((step, index) => (
            <div key={index} className="flex items-center">
              <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                currentStep > index + 1 ? 'bg-green-500 text-white' :
                currentStep === index + 1 ? 'bg-primary-500 text-white' :
                'bg-gray-200 text-gray-600'
              }`}>
                {currentStep > index + 1 ? '' : index + 1}
              </div>
              <span className={`ml-2 text-sm font-medium ${
                currentStep >= index + 1 ? 'text-gray-900' : 'text-gray-500'
              }`}>
                {step}
              </span>
              {index < 3 && (
                <div className={`w-8 h-0.5 mx-4 ${
                  currentStep > index + 1 ? 'bg-green-500' : 'bg-gray-200'
                }`}></div>
              )}
            </div>
          ))}
        </div>
      </div>

      {/* Step 1: Search Form */}
      {currentStep === 1 && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold mb-4">Search Available Trips</h3>

          <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
            <div>
              <label className="label">From</label>
              <select
                value={searchForm.origin}
                onChange={(e) => setSearchForm(prev => ({ ...prev, origin: e.target.value }))}
                className="input w-full"
                required
              >
                <option value="">Select origin</option>
                {locations.map(location => (
                  <option key={location.id} value={location.id}>{location.city}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="label">To</label>
              <select
                value={searchForm.destination}
                onChange={(e) => setSearchForm(prev => ({ ...prev, destination: e.target.value }))}
                className="input w-full"
                required
              >
                <option value="">Select destination</option>
                {locations.map(location => (
                  <option key={location.id} value={location.id}>{location.city}</option>
                ))}
              </select>
            </div>

            <div>
              <label className="label">Departure Date</label>
              <input
                type="date"
                value={searchForm.date}
                onChange={(e) => setSearchForm(prev => ({ ...prev, date: e.target.value }))}
                min={format(new Date(), 'yyyy-MM-dd')}
                max={format(addDays(new Date(), 90), 'yyyy-MM-dd')}
                className="input w-full"
                required
              />
            </div>

            <div>
              <label className="label">Passengers</label>
              <select
                value={customerInfo.passengerCount}
                onChange={(e) => setCustomerInfo(prev => ({ ...prev, passengerCount: parseInt(e.target.value) }))}
                className="input w-full"
              >
                {[1,2,3,4,5,6].map(num => (
                  <option key={num} value={num}>{num} passenger{num > 1 ? 's' : ''}</option>
                ))}
              </select>
            </div>
          </div>

          <button
            onClick={handleSearch}
            disabled={!searchForm.origin || !searchForm.destination || isProcessing}
            className="btn-primary text-lg px-8 py-3"
          >
            {isProcessing ? 'Searching...' : 'Search Trips'}
          </button>
        </div>
      )}

      {/* Step 2: Trip Selection */}
      {currentStep === 2 && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold mb-4">Available Trips ({searchResults.length} found)</h3>

          {searchResults.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <p>No trips found for the selected route and date.</p>
              <button onClick={resetSearch} className="btn-primary mt-4">Try Another Search</button>
            </div>
          ) : (
            <div className="space-y-4">
              {searchResults.map(trip => (
                <div key={trip.id} className="border border-gray-200 rounded-lg p-4 hover:border-primary-300 cursor-pointer"
                     onClick={() => handleTripSelect(trip)}>
                  <div className="flex items-center justify-between">
                    <div className="flex-1">
                      <div className="flex items-center space-x-4">
                        <div>
                          <div className="font-semibold text-lg">{trip.route.name}</div>
                          <div className="text-sm text-gray-600">
                            {locations.find(l => l.id === trip.route.originId)?.city}  
                            {locations.find(l => l.id === trip.route.destinationId)?.city}
                          </div>
                        </div>
                        <div className="text-center">
                          <div className="font-semibold">{formatTime(trip.departureDatetime)}</div>
                          <div className="text-sm text-gray-600">Departure</div>
                        </div>
                        <div className="text-center">
                          <div className="font-semibold">{formatTime(trip.arrivalDatetime)}</div>
                          <div className="text-sm text-gray-600">Arrival</div>
                        </div>
                      </div>
                    </div>
                    <div className="text-right">
                      <div className="text-2xl font-bold text-primary-600">RM {trip.route.basePrice}</div>
                      <div className="text-sm text-gray-600">{trip.availableSeats} seats available</div>
                      <div className={`text-xs px-2 py-1 rounded-full ${
                        calculateTripOccupancy(trip) > 80 ? 'bg-red-100 text-red-600' :
                        calculateTripOccupancy(trip) > 60 ? 'bg-yellow-100 text-yellow-600' :
                        'bg-green-100 text-green-600'
                      }`}>
                        {calculateTripOccupancy(trip)}% occupied
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* Step 3: Customer Information */}
      {currentStep === 3 && selectedTrip && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold mb-4">Customer Information</h3>

          {/* Selected Trip Summary */}
          <div className="bg-gray-50 rounded-lg p-4 mb-6">
            <h4 className="font-semibold mb-2">Selected Trip</h4>
            <div className="flex items-center justify-between">
              <div>
                <div className="font-medium">{selectedTrip.route.name}</div>
                <div className="text-sm text-gray-600">
                  {formatDateTime(selectedTrip.departureDatetime)}
                </div>
              </div>
              <div className="text-right">
                <div className="text-lg font-bold text-primary-600">RM {selectedTrip.route.basePrice * customerInfo.passengerCount}</div>
                <div className="text-sm text-gray-600">{customerInfo.passengerCount} passenger{customerInfo.passengerCount > 1 ? 's' : ''}</div>
              </div>
            </div>
          </div>

          {/* Customer Form */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
            <div>
              <label className="label">Full Name *</label>
              <input
                type="text"
                value={customerInfo.fullName}
                onChange={(e) => setCustomerInfo(prev => ({ ...prev, fullName: e.target.value }))}
                className="input w-full"
                placeholder="Enter customer's full name"
                required
              />
            </div>

            <div>
              <label className="label">Phone Number *</label>
              <input
                type="tel"
                value={customerInfo.phone}
                onChange={(e) => setCustomerInfo(prev => ({ ...prev, phone: e.target.value }))}
                className="input w-full"
                placeholder="+60123456789"
                required
              />
            </div>

            <div>
              <label className="label">Email Address</label>
              <input
                type="email"
                value={customerInfo.email}
                onChange={(e) => setCustomerInfo(prev => ({ ...prev, email: e.target.value }))}
                className="input w-full"
                placeholder="customer@email.com"
              />
            </div>

            <div>
              <label className="label">IC Number</label>
              <input
                type="text"
                value={customerInfo.icNumber}
                onChange={(e) => setCustomerInfo(prev => ({ ...prev, icNumber: e.target.value }))}
                className="input w-full"
                placeholder="123456-78-9012"
              />
            </div>
          </div>

          <div className="flex space-x-4">
            <button onClick={() => setCurrentStep(2)} className="btn-secondary">
              Back to Trip Selection
            </button>
            <button 
              onClick={handleCustomerSubmit}
              disabled={!customerInfo.fullName || !customerInfo.phone}
              className="btn-primary"
            >
              Continue to Payment
            </button>
          </div>
        </div>
      )}

      {/* Step 4: Payment */}
      {currentStep === 4 && selectedTrip && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold mb-4">Payment & Confirmation</h3>

          {/* Booking Summary */}
          <div className="bg-gray-50 rounded-lg p-6 mb-6">
            <h4 className="font-semibold mb-4">Booking Summary</h4>

            <div className="space-y-3">
              <div className="flex justify-between">
                <span>Route:</span>
                <span className="font-medium">{selectedTrip.route.name}</span>
              </div>
              <div className="flex justify-between">
                <span>Date & Time:</span>
                <span className="font-medium">
                  {formatDateTime(selectedTrip.departureDatetime)}
                </span>
              </div>
              <div className="flex justify-between">
                <span>Passenger:</span>
                <span className="font-medium">{customerInfo.fullName}</span>
              </div>
              <div className="flex justify-between">
                <span>Phone:</span>
                <span className="font-medium">{customerInfo.phone}</span>
              </div>
              <div className="flex justify-between">
                <span>Passengers:</span>
                <span className="font-medium">{customerInfo.passengerCount}</span>
              </div>
              <hr />
              <div className="flex justify-between text-lg font-bold">
                <span>Total Amount:</span>
                <span className="text-primary-600">RM {selectedTrip.route.basePrice * customerInfo.passengerCount}</span>
              </div>
            </div>
          </div>

          {/* Payment Methods */}
          <div className="mb-6">
            <h4 className="font-semibold mb-3">Payment Method</h4>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
              <button className="border-2 border-primary-500 bg-primary-50 text-primary-700 rounded-lg p-4 text-center">
                 Card Payment
              </button>
              <button className="border-2 border-gray-200 hover:border-gray-300 rounded-lg p-4 text-center">
                 Cash Payment
              </button>
              <button className="border-2 border-gray-200 hover:border-gray-300 rounded-lg p-4 text-center">
                 E-Wallet
              </button>
            </div>
          </div>

          <div className="flex space-x-4">
            <button onClick={() => setCurrentStep(3)} className="btn-secondary">
              Back to Customer Info
            </button>
            <button onClick={handlePaymentComplete} className="btn-primary">
              Confirm Payment & Issue Ticket
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminDashboard;
