import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Bus, 
  Route, 
  Calendar,
  Clock,
  MapPin,
  Settings,
  BarChart3,
  Activity,
  Plus,
  Edit,
  Trash2,
  Map,
  Info,
  ArrowRight,
  TrendingUp,
  Users,
  Loader
} from 'lucide-react';
import { 
  getDashboardStats, 
  getAnalyticsData, 
  getVehicles, 
  getRoutes, 
  getTodaysSchedule,
  searchTrips,
  getLocations,
  formatCurrency,
  formatDate,
  formatTime,
  calculateOccupancy,
  getOccupancyColorClass,
  handleApiError
} from '../../services/adminApi';
import { useApp } from '../../context/AppContext';
import RouteMap from '../../components/admin/RouteMap';
import RouteDetails from '../../components/admin/RouteDetails';
import { format, addDays } from 'date-fns';

const AdminDashboard = ({ activeTab = 'customer-service', setActiveTab }) => {
  const { locations } = useApp();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [stats, setStats] = useState({
    activeRoutes: 0,
    activeTrips: 0,
    fleetSize: 0,
    scheduledTrips: 0
  });

  const [analyticsData, setAnalyticsData] = useState({
    totalRevenue: 0,
    averageOccupancy: 0,
    totalPassengers: 0
  });

  const [routes, setRoutes] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [todaysSchedule, setTodaysSchedule] = useState([]);
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [showRouteDetails, setShowRouteDetails] = useState(false);
  const [detailRoute, setDetailRoute] = useState(null);

  useEffect(() => {
    loadDashboardData();
  }, []);

  const loadDashboardData = async () => {
    setLoading(true);
    try {
      // Load all dashboard data in parallel
      const [
        statsResponse,
        analyticsResponse,
        routesResponse,
        vehiclesResponse,
        scheduleResponse
      ] = await Promise.all([
        getDashboardStats(),
        getAnalyticsData(),
        getRoutes(),
        getVehicles(),
        getTodaysSchedule()
      ]);

      if (statsResponse.success) {
        setStats(statsResponse.data);
      }

      if (analyticsResponse.success) {
        setAnalyticsData(analyticsResponse.data);
      }

      if (routesResponse.success) {
        setRoutes(routesResponse.data);
      }

      if (vehiclesResponse.success) {
        setVehicles(vehiclesResponse.data);
      }

      if (scheduleResponse.success) {
        setTodaysSchedule(scheduleResponse.data);
      }

      setError(null);
    } catch (err) {
      console.error('Error loading dashboard data:', err);
      setError(handleApiError(err));
    } finally {
      setLoading(false);
    }
  };

  const handleRouteClick = (route) => {
    setSelectedRoute(route);
  };

  const handleViewRoute = (route) => {
    setDetailRoute(route);
    setShowRouteDetails(true);
  };

  const clearSelection = () => {
    setSelectedRoute(null);
  };

  if (loading) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="flex items-center justify-center h-64">
          <Loader className="w-8 h-8 animate-spin text-primary-600" />
          <span className="ml-2 text-gray-600">Loading dashboard data...</span>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-red-400" viewBox="0 0 20 20" fill="currentColor">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">Error loading dashboard</h3>
              <div className="mt-2 text-sm text-red-700">
                <p>{error}</p>
                <button 
                  onClick={loadDashboardData}
                  className="mt-2 text-red-800 underline hover:text-red-900"
                >
                  Try again
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Tab Content */}
      {activeTab === 'customer-service' && (
        <CustomerService />
      )}

      {activeTab === 'overview' && (
        <>
          {/* Action Buttons */}
          <div className="flex justify-end mb-6">
            <div className="flex space-x-3">
              <button 
                onClick={() => setActiveTab('analytics')}
                className="btn-secondary flex items-center"
              >
                <BarChart3 className="w-4 h-4 mr-2" />
                Analytics
              </button>
              <button 
                onClick={() => setActiveTab('schedule')}
                className="btn-primary flex items-center"
              >
                <Plus className="w-4 h-4 mr-2" />
                Add Route
              </button>
            </div>
          </div>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <div className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Active Routes</p>
                  <p className="text-3xl font-bold text-primary-600">{stats.activeRoutes}</p>
                </div>
                <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center">
                  <Route className="w-6 h-6 text-primary-600" />
                </div>
              </div>
            </div>

            <div className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Active Trips</p>
                  <p className="text-3xl font-bold text-success-600">{stats.activeTrips}</p>
                </div>
                <div className="w-12 h-12 bg-success-100 rounded-full flex items-center justify-center">
                  <Bus className="w-6 h-6 text-success-600" />
                </div>
              </div>
            </div>

            <div className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Fleet Size</p>
                  <p className="text-3xl font-bold text-warning-600">{stats.fleetSize}</p>
                </div>
                <div className="w-12 h-12 bg-warning-100 rounded-full flex items-center justify-center">
                  <Settings className="w-6 h-6 text-warning-600" />
                </div>
              </div>
            </div>

            <div className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Scheduled Trips</p>
                  <p className="text-3xl font-bold text-purple-600">{stats.scheduledTrips}</p>
                </div>
                <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center">
                  <Calendar className="w-6 h-6 text-purple-600" />
                </div>
              </div>
            </div>
          </div>

          {/* Management Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <div className="card hover:shadow-lg transition-shadow cursor-pointer" onClick={() => setActiveTab('vehicles')}>
              <div className="flex items-center justify-between mb-4">
                <div className="w-12 h-12 bg-blue-100 rounded-full flex items-center justify-center">
                  <Bus className="w-6 h-6 text-blue-600" />
                </div>
                <ArrowRight className="w-5 h-5 text-gray-400" />
              </div>
              <h3 className="font-semibold text-lg text-gray-900 mb-2">Manage Vehicles</h3>
              <p className="text-gray-600 text-sm">Add, edit, or maintain vehicles</p>
            </div>

            <div className="card hover:shadow-lg transition-shadow cursor-pointer" onClick={() => setActiveTab('schedule')}>
              <div className="flex items-center justify-between mb-4">
                <div className="w-12 h-12 bg-green-100 rounded-full flex items-center justify-center">
                  <MapPin className="w-6 h-6 text-green-600" />
                </div>
                <ArrowRight className="w-5 h-5 text-gray-400" />
              </div>
              <h3 className="font-semibold text-lg text-gray-900 mb-2">Route Planning</h3>
              <p className="text-gray-600 text-sm">Create and optimize routes</p>
            </div>

            <div className="card hover:shadow-lg transition-shadow cursor-pointer" onClick={() => setActiveTab('schedule')}>
              <div className="flex items-center justify-between mb-4">
                <div className="w-12 h-12 bg-yellow-100 rounded-full flex items-center justify-center">
                  <Calendar className="w-6 h-6 text-yellow-600" />
                </div>
                <ArrowRight className="w-5 h-5 text-gray-400" />
              </div>
              <h3 className="font-semibold text-lg text-gray-900 mb-2">Schedule Trips</h3>
              <p className="text-gray-600 text-sm">Plan daily operations</p>
            </div>

            <div className="card hover:shadow-lg transition-shadow cursor-pointer" onClick={() => setActiveTab('settings')}>
              <div className="flex items-center justify-between mb-4">
                <div className="w-12 h-12 bg-purple-100 rounded-full flex items-center justify-center">
                  <Settings className="w-6 h-6 text-purple-600" />
                </div>
                <ArrowRight className="w-5 h-5 text-gray-400" />
              </div>
              <h3 className="font-semibold text-lg text-gray-900 mb-2">System Settings</h3>
              <p className="text-gray-600 text-sm">Configure system parameters</p>
            </div>
          </div>

          {/* Active Routes and Map */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
            {/* Active Routes */}
            <div className="card">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold">Active Routes</h3>
                <button onClick={clearSelection} className="btn-secondary text-sm">
                  Clear Selection
                </button>
              </div>

              <div className="space-y-3 max-h-96 overflow-y-auto">
                {routes.map((route) => (
                  <div 
                    key={route.id}
                    className={`p-4 border rounded-lg cursor-pointer transition-colors ${
                      selectedRoute?.id === route.id ? 'border-primary-500 bg-primary-50' : 'border-gray-200 hover:border-gray-300'
                    }`}
                    onClick={() => handleRouteClick(route)}
                  >
                    <div className="flex items-center justify-between">
                      <div className="flex-1">
                        <div className="flex items-center justify-between mb-2">
                          <h4 className="font-medium text-gray-900">{route.name}</h4>
                          <div className="flex space-x-2">
                            <button 
                              onClick={(e) => {
                                e.stopPropagation();
                                handleViewRoute(route);
                              }}
                              className="text-primary-600 hover:text-primary-700"
                              title="View Details"
                            >
                              <BarChart3 className="w-4 h-4" />
                            </button>
                            <button className="text-gray-400 hover:text-gray-600" title="Edit">
                              <Edit className="w-4 h-4" />
                            </button>
                            <button className="text-red-400 hover:text-red-600" title="Delete">
                              <Trash2 className="w-4 h-4" />
                            </button>
                          </div>
                        </div>
                        <div className="text-sm text-gray-600 space-y-1">
                          <div className="flex items-center">
                            <MapPin className="w-4 h-4 mr-2" />
                            {locations.find(l => l.id === route.originId)?.city} → {locations.find(l => l.id === route.destinationId)?.city}
                          </div>
                          <div className="flex items-center justify-between">
                            <span>Distance: {route.distanceKm} km</span>
                            <span>Duration: {Math.floor(route.durationHours)}h {Math.round((route.durationHours % 1) * 60)}m</span>
                            <span className="text-primary-600 font-medium">Price: RM {route.basePrice}</span>
                          </div>

                          {/* Trip Occupation for this route */}
                          <div className="mt-2">
                            {(() => {
                              const routeTrips = todaysSchedule.filter(trip => trip.routeId === route.id);
                              const avgOccupation = routeTrips.length > 0 
                                ? routeTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0) / routeTrips.length
                                : 0;

                              return avgOccupation > 0 && (
                                <div className="flex items-center justify-between text-xs">
                                  <span className="text-gray-500">Avg. Occupation:</span>
                                  <div className="flex items-center space-x-2">
                                    <div className="w-16 bg-gray-200 rounded-full h-2">
                                      <div 
                                        className="h-2 rounded-full transition-all"
                                        style={{ 
                                          width: `${Math.min(avgOccupation, 100)}%`,
                                          backgroundColor: avgOccupation > 80 ? '#EF4444' : avgOccupation > 60 ? '#F59E0B' : '#10B981'
                                        }}
                                      ></div>
                                    </div>
                                    <span className={`font-medium ${avgOccupation > 80 ? 'text-red-600' : avgOccupation > 60 ? 'text-yellow-600' : 'text-green-600'}`}>
                                      {avgOccupation.toFixed(0)}%
                                    </span>
                                  </div>
                                </div>
                              );
                            })()}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {/* Route Visualization */}
            <div className="card">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold">Route Visualization</h3>
                <button className="btn-secondary text-sm">
                  Clear Selection
                </button>
              </div>

              <div className="h-96 bg-gray-100 rounded-lg">
                <RouteMap 
                  routes={selectedRoute ? [selectedRoute] : routes}
                  locations={locations}
                  selectedTrips={todaysSchedule}
                />
              </div>
            </div>
          </div>
        </>
      )}

      {activeTab === 'vehicles' && (
        <VehicleManagement vehicles={vehicles} />
      )}

      {activeTab === 'schedule' && (
        <ScheduleManagement 
          todaysSchedule={todaysSchedule} 
          routes={routes} 
          vehicles={vehicles} 
        />
      )}

      {activeTab === 'analytics' && (
        <Analytics allTrips={todaysSchedule} routes={routes} />
      )}

      {activeTab === 'settings' && (
        <SystemSettings />
      )}

      {/* Route Details Modal */}
      {showRouteDetails && (
        <div style={{ zIndex: 9999 }}>
          <RouteDetails
            route={detailRoute}
            locations={locations}
            onClose={() => {
              setShowRouteDetails(false);
              setDetailRoute(null);
            }}
          />
        </div>
      )}
    </div>
  );
};

// Vehicle Management Component
const VehicleManagement = ({ vehicles = [], onRefresh }) => {
  const [showAddVehicle, setShowAddVehicle] = useState(false);
  const [editingVehicle, setEditingVehicle] = useState(null);
  const [showServiceModal, setShowServiceModal] = useState(null);

  const handleAddVehicle = () => {
    setShowAddVehicle(true);
  };

  const handleEditVehicle = (vehicle) => {
    setEditingVehicle(vehicle);
  };

  const handleServiceVehicle = (vehicle) => {
    setShowServiceModal(vehicle);
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Vehicle Management</h2>
        <button 
          className="btn-primary flex items-center"
          onClick={handleAddVehicle}
        >
          <Plus className="w-4 h-4 mr-2" />
          Add Vehicle
        </button>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {vehicles.map((vehicle) => (
          <div key={vehicle.id} className="card">
            <div className="flex items-center justify-between mb-4">
              <div className="flex items-center space-x-3">
                <div className="w-10 h-10 bg-blue-100 rounded-full flex items-center justify-center">
                  <Bus className="w-5 h-5 text-blue-600" />
                </div>
                <div>
                  <h3 className="font-semibold text-gray-900">{vehicle.license_plate || vehicle.plateNumber || vehicle.vehicle_number}</h3>
                  <p className="text-sm text-gray-600">{vehicle.model || 'N/A'}</p>
                </div>
              </div>
              <span className={`px-2 py-1 rounded-full text-xs font-medium ${
                vehicle.status === 'active' ? 'bg-green-100 text-green-800' :
                vehicle.status === 'maintenance' ? 'bg-yellow-100 text-yellow-800' :
                'bg-red-100 text-red-800'
              }`}>
                {vehicle.status}
              </span>
            </div>

            <div className="space-y-2 text-sm">
              <div className="flex justify-between">
                <span className="text-gray-600">Capacity:</span>
                <span className="font-medium">{vehicle.vehicle_capacity || vehicle.capacity || 'N/A'} seats</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Mileage:</span>
                <span className="font-medium">{vehicle.current_mileage ? vehicle.current_mileage.toLocaleString() : 'N/A'} km</span>
              </div>
              <div className="flex justify-between">
                <span className="text-gray-600">Last Service:</span>
                <span className="font-medium">
                  {vehicle.last_service_date 
                    ? new Date(vehicle.last_service_date).toLocaleDateString('en-GB')
                    : vehicle.lastService || 'N/A'}
                </span>
              </div>
            </div>

            <div className="mt-4 flex space-x-2">
              <button 
                className="btn-secondary text-xs flex-1"
                onClick={() => handleEditVehicle(vehicle)}
              >
                Edit
              </button>
              <button 
                className="btn-secondary text-xs flex-1"
                onClick={() => handleServiceVehicle(vehicle)}
              >
                Service
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Schedule Management Component
const ScheduleManagement = ({ todaysSchedule = [], routes = [], vehicles = [] }) => {
  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Schedule Management</h2>
        <button className="btn-primary flex items-center">
          <Calendar className="w-4 h-4 mr-2" />
          Create Schedule
        </button>
      </div>

      <div className="card">
        <h3 className="text-lg font-semibold mb-4">Today's Schedule</h3>
        <div className="space-y-3">
          {todaysSchedule.map((trip) => {
            const route = routes.find(r => r.id === trip.routeId);
            const vehicle = vehicles.find(v => v.id === trip.vehicleId);
            return (
              <div key={trip.id} className="p-4 border border-gray-200 rounded-lg">
                <div className="flex items-center justify-between">
                  <div className="flex-1">
                    <div className="flex items-center space-x-4">
                      <div className="text-sm">
                        <div className="font-medium text-gray-900">{route?.name}</div>
                        <div className="text-gray-600">{vehicle?.plateNumber} • {vehicle?.model}</div>
                      </div>
                      <div className="text-sm text-gray-600">
                        {trip.departureDatetime.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})} - 
                        {trip.arrivalDatetime.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                      </div>
                    </div>
                  </div>
                  <div className="flex items-center space-x-4">
                    <div className="text-sm text-right">
                      <div className="font-medium">{calculateOccupancy(trip)}/{trip.totalSeats} seats</div>
                      <div className={`text-xs px-2 py-1 rounded-full ${
                        calculateOccupancy(trip) > 80 ? 'bg-red-100 text-red-600' :
                        calculateOccupancy(trip) > 60 ? 'bg-yellow-100 text-yellow-600' :
                        'bg-green-100 text-green-600'
                      }`}>
                        {calculateOccupancy(trip)}% occupied
                      </div>
                    </div>
                    <div className="w-16 bg-gray-200 rounded-full h-2">
                      <div 
                        className="h-2 rounded-full transition-all"
                        style={{ 
                          width: `${Math.min(calculateOccupancy(trip), 100)}%`,
                          backgroundColor: calculateOccupancy(trip) > 80 ? '#EF4444' : calculateOccupancy(trip) > 60 ? '#F59E0B' : '#10B981'
                        }}
                      ></div>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

// Analytics Component
const Analytics = ({ allTrips, routes }) => {
  const totalRevenue = allTrips.reduce((sum, trip) => {
    const route = routes.find(r => r.id === trip.routeId);
    return sum + (route?.basePrice || 0) * calculateOccupancy(trip);
  }, 0);

  const avgOccupancy = allTrips.length > 0 
    ? allTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0) / allTrips.length
    : 0;

  return (
    <div className="space-y-6">
      <h2 className="text-2xl font-bold text-gray-900">Analytics & Reports</h2>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Total Revenue</p>
              <p className="text-2xl font-bold text-green-600">RM {totalRevenue.toLocaleString()}</p>
            </div>
            <TrendingUp className="w-8 h-8 text-green-600" />
          </div>
        </div>

        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Average Occupancy</p>
              <p className="text-2xl font-bold text-blue-600">{avgOccupancy.toFixed(1)}%</p>
            </div>
            <Users className="w-8 h-8 text-blue-600" />
          </div>
        </div>

        <div className="card">
          <div className="flex items-center justify-between">
            <div>
              <p className="text-sm text-gray-600">Total Passengers</p>
              <p className="text-2xl font-bold text-purple-600">{allTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0)}</p>
            </div>
            <Users className="w-8 h-8 text-purple-600" />
          </div>
        </div>
      </div>

      <div className="card">
        <h3 className="text-lg font-semibold mb-4">Route Performance</h3>
        <div className="space-y-4">
          {routes.map((route) => {
            const routeTrips = allTrips.filter(trip => trip.routeId === route.id);
            const routeRevenue = routeTrips.reduce((sum, trip) => sum + route.basePrice * calculateOccupancy(trip), 0);
            const routeOccupancy = routeTrips.length > 0 
              ? routeTrips.reduce((sum, trip) => sum + calculateOccupancy(trip), 0) / routeTrips.length
              : 0;

            return (
              <div key={route.id} className="p-4 border border-gray-200 rounded-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <h4 className="font-medium text-gray-900">{route.name}</h4>
                    <p className="text-sm text-gray-600">{routeTrips.length} trips scheduled</p>
                  </div>
                  <div className="text-right space-y-1">
                    <div className="text-sm font-medium text-green-600">RM {routeRevenue.toLocaleString()}</div>
                    <div className="flex items-center space-x-2">
                      <div className="w-20 bg-gray-200 rounded-full h-2">
                        <div 
                          className="h-2 rounded-full transition-all"
                          style={{ 
                            width: `${Math.min(routeOccupancy, 100)}%`,
                            backgroundColor: routeOccupancy > 80 ? '#EF4444' : routeOccupancy > 60 ? '#F59E0B' : '#10B981'
                          }}
                        ></div>
                      </div>
                      <span className="text-xs font-medium">{routeOccupancy.toFixed(0)}%</span>
                    </div>
                  </div>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

