import React, { useState, useEffect } from 'react';
import { 
  MapPin, 
  Plus, 
  Pencil, 
  Trash2, 
  Search,
  X,
  ArrowRight,
  DollarSign,
  Clock
} from 'lucide-react';
import { 
  getAllRoutes, 
  getAllLocations,
  createRoute, 
  updateRoute, 
  deleteRoute 
} from '../../services/adminApi';

const RouteManagement = () => {
  const [routes, setRoutes] = useState([]);
  const [locations, setLocations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showForm, setShowForm] = useState(false);
  const [editingRoute, setEditingRoute] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    basePrice: 0,
    estimatedDuration: 120, // 默认2小时，单位分钟
    stops: []
  });

  // 加载所有路线和站点
  const fetchData = async () => {
    setLoading(true);
    try {
      const [routesRes, locationsRes] = await Promise.all([
        getAllRoutes(),
        getAllLocations()
      ]);

      if (routesRes.success) {
        setRoutes(routesRes.data);
      } else {
        setError(routesRes.message || 'Failed to load routes');
      }

      if (locationsRes.success) {
        setLocations(locationsRes.data);
      } else {
        setError(locationsRes.message || 'Failed to load locations');
      }
    } catch (err) {
      setError('Error connecting to server');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  // 处理表单输入变化
  const handleInputChange = (e) => {
    const { name, value, type } = e.target;
    const numValue = type === 'number' ? parseFloat(value) : value;

    setFormData(prev => ({
      ...prev,
      [name]: numValue
    }));
  };

  // 添加站点到路线
  const addStopToRoute = (locationId) => {
    const location = locations.find(loc => loc.id === locationId);
    if (!location) return;

    const isFirstStop = formData.stops.length === 0;

    const newStop = {
      locationId,
      name: location.name,
      city: location.city,
      isOrigin: isFirstStop,
      isDestination: false,
      arrivalOffsetMinutes: isFirstStop ? 0 : formData.stops[formData.stops.length - 1].departureOffsetMinutes + 60,
      departureOffsetMinutes: isFirstStop ? 10 : formData.stops[formData.stops.length - 1].departureOffsetMinutes + 70,
      priceFromOrigin: isFirstStop ? 0 : formData.basePrice * (formData.stops.length * 0.5)
    };

    setFormData(prev => ({
      ...prev,
      stops: [...prev.stops, newStop]
    }));
  };

  // 设置站点为终点站
  const setStopAsDestination = (index) => {
    setFormData(prev => {
      const updatedStops = prev.stops.map((stop, i) => ({
        ...stop,
        isDestination: i === index
      }));
      return { ...prev, stops: updatedStops };
    });
  };

  // 从路线中移除站点
  const removeStop = (index) => {
    setFormData(prev => {
      const updatedStops = [...prev.stops];
      updatedStops.splice(index, 1);

      // 如果删除了起点，将下一个站点设为起点
      if (index === 0 && updatedStops.length > 0) {
        updatedStops[0] = { ...updatedStops[0], isOrigin: true, priceFromOrigin: 0 };
      }

      // 如果删除了终点，清除终点标记
      const hadDestination = prev.stops.some(s => s.isDestination);
      if (hadDestination && !updatedStops.some(s => s.isDestination) && updatedStops.length > 0) {
        updatedStops[updatedStops.length - 1] = {
          ...updatedStops[updatedStops.length - 1],
          isDestination: true
        };
      }

      return { ...prev, stops: updatedStops };
    });
  };

  // 更新站点信息
  const updateStopInfo = (index, field, value) => {
    setFormData(prev => {
      const updatedStops = [...prev.stops];
      updatedStops[index] = { ...updatedStops[index], [field]: value };
      return { ...prev, stops: updatedStops };
    });
  };

  // 打开编辑表单
  const handleEdit = (route) => {
    // 获取站点详细信息
    const routeStops = route.stops?.map(stop => {
      const location = locations.find(l => l.id === stop.locationId);
      return {
        ...stop,
        name: location?.name || 'Unknown',
        city: location?.city || ''
      };
    }) || [];

    setEditingRoute(route);
    setFormData({
      name: route.name,
      description: route.description || '',
      basePrice: route.basePrice || 0,
      estimatedDuration: route.estimatedDuration || 120,
      stops: routeStops
    });
    setShowForm(true);
  };

  // 添加新路线
  const handleAdd = () => {
    setEditingRoute(null);
    setFormData({
      name: '',
      description: '',
      basePrice: 0,
      estimatedDuration: 120,
      stops: []
    });
    setShowForm(true);
  };

  // 删除路线
  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this route?')) return;

    setLoading(true);
    try {
      const response = await deleteRoute(id);
      if (response.success) {
        setRoutes(routes.filter(route => route.id !== id));
      } else {
        setError(response.message || 'Failed to delete route');
      }
    } catch (err) {
      setError('Error connecting to server');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // 提交表单
  const handleSubmit = async (e) => {
    e.preventDefault();

    // 验证表单
    if (!formData.name) {
      alert('Route name is required');
      return;
    }

    if (formData.stops.length < 2) {
      alert('Route must have at least two stops (origin and destination)');
      return;
    }

    if (!formData.stops.some(stop => stop.isOrigin)) {
      alert('Route must have an origin stop');
      return;
    }

    if (!formData.stops.some(stop => stop.isDestination)) {
      alert('Route must have a destination stop');
      return;
    }

    setLoading(true);
    try {
      let response;

      if (editingRoute) {
        response = await updateRoute(editingRoute.id, formData);
      } else {
        response = await createRoute(formData);
      }

      if (response.success) {
        await fetchData();
        setShowForm(false);
      } else {
        setError(response.message || 'Failed to save route');
      }
    } catch (err) {
      setError('Error connecting to server');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  // 过滤路线
  const filteredRoutes = routes.filter(route => 
    route.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    route.description?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 获取路线的起点和终点名称
  const getRouteEndpoints = (route) => {
    const origin = route.stops?.find(stop => stop.isOrigin);
    const destination = route.stops?.find(stop => stop.isDestination);

    const originLocation = origin ? locations.find(l => l.id === origin.locationId) : null;
    const destinationLocation = destination ? locations.find(l => l.id === destination.locationId) : null;

    return {
      origin: originLocation?.name || 'Unknown',
      destination: destinationLocation?.name || 'Unknown'
    };
  };

  return (
    <div className="container mx-auto px-4 py-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Route Management</h1>
        <button 
          onClick={handleAdd}
          className="btn-primary flex items-center"
        >
          <Plus className="w-4 h-4 mr-1" />
          Add Route
        </button>
      </div>

      {error && (
        <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded mb-4">
          {error}
        </div>
      )}

      <div className="mb-6">
        <div className="relative">
          <input
            type="text"
            placeholder="Search routes by name or description..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="w-full px-4 py-2 pl-10 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <Search className="w-5 h-5 text-gray-400 absolute left-3 top-2.5" />
          {searchTerm && (
            <button
              onClick={() => setSearchTerm('')}
              className="absolute right-3 top-2.5"
            >
              <X className="w-5 h-5 text-gray-400" />
            </button>
          )}
        </div>
      </div>

      {showForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg shadow-lg p-6 w-full max-w-4xl max-h-[90vh] overflow-y-auto">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-xl font-bold">
                {editingRoute ? 'Edit Route' : 'Add New Route'}
              </h2>
              <button onClick={() => setShowForm(false)}>
                <X className="w-5 h-5" />
              </button>
            </div>

            <form onSubmit={handleSubmit} className="space-y-6">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Route Name
                  </label>
                  <input
                    type="text"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                    placeholder="e.g. KL Express"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Base Price (RM)
                  </label>
                  <input
                    type="number"
                    name="basePrice"
                    value={formData.basePrice}
                    onChange={handleInputChange}
                    min="0"
                    step="0.01"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Estimated Duration (minutes)
                  </label>
                  <input
                    type="number"
                    name="estimatedDuration"
                    value={formData.estimatedDuration}
                    onChange={handleInputChange}
                    min="1"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Description
                  </label>
                  <input
                    type="text"
                    name="description"
                    value={formData.description}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    placeholder="Optional route description"
                  />
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Add Stops
                </label>
                <select
                  onChange={(e) => {
                    if (e.target.value) {
                      addStopToRoute(e.target.value);
                      e.target.value = '';
                    }
                  }}
                  value=""
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="">Select a location to add</option>
                  {locations.map(location => (
                    <option key={location.id} value={location.id}>
                      {location.name} ({location.city})
                    </option>
                  ))}
                </select>
              </div>

              <div className="border rounded-md p-4">
                <h3 className="font-medium mb-3">Route Stops</h3>

                {formData.stops.length === 0 ? (
                  <p className="text-sm text-gray-500">No stops added yet. Add stops to create your route.</p>
                ) : (
                  <div className="space-y-4">
                    {formData.stops.map((stop, index) => (
                      <div key={index} className="border-b pb-4">
                        <div className="flex items-start justify-between">
                          <div className="flex items-start">
                            <div className={`w-6 h-6 rounded-full flex items-center justify-center mr-3 ${
                              stop.isOrigin ? 'bg-green-100 text-green-600' : 
                              stop.isDestination ? 'bg-red-100 text-red-600' : 
                              'bg-blue-100 text-blue-600'
                            }`}>
                              {index + 1}
                            </div>
                            <div>
                              <div className="font-medium">{stop.name}</div>
                              <div className="text-xs text-gray-500">{stop.city}</div>
                              <div className="flex items-center text-xs text-gray-500 mt-1">
                                <span className="font-medium mr-2">
                                  {stop.isOrigin ? 'Origin' : stop.isDestination ? 'Destination' : 'Intermediate Stop'}
                                </span>
                              </div>
                            </div>
                          </div>
                          <div className="flex space-x-2">
                            {!stop.isOrigin && !stop.isDestination && (
                              <button
                                type="button"
                                onClick={() => setStopAsDestination(index)}
                                className="text-xs text-blue-600 hover:text-blue-800"
                              >
                                Set as destination
                              </button>
                            )}
                            <button
                              type="button"
                              onClick={() => removeStop(index)}
                              className="text-xs text-red-600 hover:text-red-800"
                            >
                              Remove
                            </button>
                          </div>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mt-3">
                          {!stop.isOrigin && (
                            <div>
                              <label className="block text-xs font-medium text-gray-700 mb-1">
                                Arrival Time (minutes from departure)
                              </label>
                              <input
                                type="number"
                                value={stop.arrivalOffsetMinutes}
                                onChange={(e) => updateStopInfo(index, 'arrivalOffsetMinutes', parseInt(e.target.value))}
                                min="0"
                                className="w-full px-3 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                              />
                            </div>
                          )}

                          {!stop.isDestination && (
                            <div>
                              <label className="block text-xs font-medium text-gray-700 mb-1">
                                Departure Time (minutes from origin)
                              </label>
                              <input
                                type="number"
                                value={stop.departureOffsetMinutes}
                                onChange={(e) => updateStopInfo(index, 'departureOffsetMinutes', parseInt(e.target.value))}
                                min={stop.isOrigin ? 0 : stop.arrivalOffsetMinutes}
                                className="w-full px-3 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                              />
                            </div>
                          )}

                          {!stop.isOrigin && (
                            <div>
                              <label className="block text-xs font-medium text-gray-700 mb-1">
                                Price from Origin (RM)
                              </label>
                              <input
                                type="number"
                                value={stop.priceFromOrigin}
                                onChange={(e) => updateStopInfo(index, 'priceFromOrigin', parseFloat(e.target.value))}
                                min="0"
                                step="0.01"
                                className="w-full px-3 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500 text-sm"
                              />
                            </div>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>

              <div className="flex justify-end space-x-4 pt-4">
                <button
                  type="button"
                  onClick={() => setShowForm(false)}
                  className="btn-secondary"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="btn-primary"
                  disabled={loading}
                >
                  {loading ? 'Saving...' : 'Save Route'}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {loading && !showForm ? (
        <div className="text-center py-10">
          <div className="spinner"></div>
          <p className="mt-2 text-gray-600">Loading routes...</p>
        </div>
      ) : (
        <div className="bg-white shadow rounded-lg overflow-hidden">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Route
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Stops
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Base Price
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Duration
                </th>
                <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {filteredRoutes.length === 0 ? (
                <tr>
                  <td colSpan="5" className="px-6 py-4 text-center text-gray-500">
                    No routes found
                  </td>
                </tr>
              ) : (
                filteredRoutes.map(route => {
                  const { origin, destination } = getRouteEndpoints(route);
                  const stopsCount = route.stops?.length || 0;

                  return (
                    <tr key={route.id}>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="font-medium text-gray-900">{route.name}</div>
                        {route.description && (
                          <div className="text-xs text-gray-500">{route.description}</div>
                        )}
                      </td>
                      <td className="px-6 py-4">
                        <div className="flex items-center">
                          <MapPin className="w-4 h-4 mr-1 text-green-600" />
                          <span className="text-gray-900">{origin}</span>
                          <ArrowRight className="w-4 h-4 mx-1" />
                          <MapPin className="w-4 h-4 mr-1 text-red-600" />
                          <span className="text-gray-900">{destination}</span>
                        </div>
                        <div className="text-xs text-gray-500 mt-1">
                          {stopsCount > 2 ? `${stopsCount - 2} intermediate stops` : 'Direct route'}
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center text-gray-900">
                          <DollarSign className="w-4 h-4 mr-1" />
                          <span>RM {parseFloat(route.basePrice).toFixed(2)}</span>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center text-gray-900">
                          <Clock className="w-4 h-4 mr-1" />
                          <span>
                            {route.estimatedDuration >= 60 
                              ? `${Math.floor(route.estimatedDuration / 60)}h ${route.estimatedDuration % 60}m` 
                              : `${route.estimatedDuration}m`}
                          </span>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-right text-sm font-medium">
                        <button
                          onClick={() => handleEdit(route)}
                          className="text-blue-600 hover:text-blue-900 mr-4"
                        >
                          <Pencil className="w-4 h-4" />
                        </button>
                        <button
                          onClick={() => handleDelete(route.id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          <Trash2 className="w-4 h-4" />
                        </button>
                      </td>
                    </tr>
                  );
                })
              )}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default RouteManagement;
