import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  Bus, 
  Route, 
  Calendar,
  Clock,
  MapPin,
  Settings,
  BarChart3,
  Activity,
  Plus,
  Edit,
  Trash2,
  Map,
  Info,
  ArrowRight,
  TrendingUp,
  Users
} from 'lucide-react';
import { mockRoutes, mockVehicles, generateMockTrips, calculateCurrentOccupancy, calculateOccupancyRate } from '../../data/mockData';
import { useApp } from '../../context/AppContext';
import RouteMap from '../../components/admin/RouteMap';
import RouteDetails from '../../components/admin/RouteDetails';
import { format, addDays } from 'date-fns';

const AdminDashboard = ({ activeTab = 'customer-service', setActiveTab }) => {
  const { locations } = useApp();
  const [stats, setStats] = useState({
    totalRoutes: 0,
    activeTrips: 0,
    totalVehicles: 0,
    scheduledTrips: 0
  });

  const [routes, setRoutes] = useState([]);
  const [upcomingTrips, setUpcomingTrips] = useState([]);
  const [allTrips, setAllTrips] = useState([]);
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [showRouteDetails, setShowRouteDetails] = useState(false);
  const [detailRoute, setDetailRoute] = useState(null);

  useEffect(() => {
    // Calculate admin-specific stats
    const trips = generateMockTrips();
    const activeRoutes = mockRoutes.filter(r => r.isActive);

    setStats({
      totalRoutes: activeRoutes.length,
      activeTrips: trips.filter(trip => trip.status === 'scheduled').length,
      totalVehicles: mockVehicles.filter(v => v.status === 'active').length,
      scheduledTrips: trips.length
    });

    setRoutes(activeRoutes);
    setUpcomingTrips(trips.slice(0, 5));
    setAllTrips(trips);
  }, []);

  const handleRouteClick = (route) => {
    setSelectedRoute(route);
  };

  const handleViewRoute = (route) => {
    setDetailRoute(route);
    setShowRouteDetails(true);
  };

  const clearSelection = () => {
    setSelectedRoute(null);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Tab Content */}
      {activeTab === 'customer-service' && (
        <CustomerService />
      )}

      {activeTab === 'overview' && (
        <>
          {/* Action Buttons */}
          <div className="flex justify-end mb-6">
            <div className="flex space-x-3">
              <button className="btn-secondary flex items-center">
                <BarChart3 className="w-4 h-4 mr-2" />
                Analytics
              </button>
              <button className="btn-primary flex items-center">
                <Plus className="w-4 h-4 mr-2" />
                Add Route
              </button>
            </div>
          </div>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <div className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Active Routes</p>
                  <p className="text-3xl font-bold text-primary-600">{stats.totalRoutes}</p>
                </div>
                <div className="w-12 h-12 bg-primary-100 rounded-full flex items-center justify-center">
                  <Route className="w-6 h-6 text-primary-600" />
                </div>
              </div>
            </div>

            <div className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Active Trips</p>
                  <p className="text-3xl font-bold text-success-600">{stats.activeTrips}</p>
                </div>
                <div className="w-12 h-12 bg-success-100 rounded-full flex items-center justify-center">
                  <Bus className="w-6 h-6 text-success-600" />
                </div>
              </div>
            </div>

            <div className="card">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm text-gray-600">Fleet Size</p>
