import React, { useState, useEffect } from 'react';
import { Star, ChevronRight, MapPin, Clock, Calendar } from 'lucide-react';
import { format } from 'date-fns';
import { getPopularTrips } from '../../services/adminApi';

const PopularTrips = ({ onTripSelect }) => {
  const [loading, setLoading] = useState(true);
  const [trips, setTrips] = useState([]);
  const [error, setError] = useState(null);

  // 备用的模拟数据，当API调用失败时使用
  const mockTrips = [
    {
      id: '1',
      route: { id: '1', name: 'KL - Johor Bahru Express' },
      origin: 'Kuala Lumpur',
      destination: 'Johor Bahru',
      departureTime: '08:00 AM',
      date: format(new Date(), 'yyyy-MM-dd'),
      price: 'RM 45.00',
      bookingCount: 124
    },
    {
      id: '2',
      route: { id: '2', name: 'KL - Penang Highway' },
      origin: 'Kuala Lumpur',
      destination: 'Penang',
      departureTime: '09:30 AM',
      date: format(new Date(), 'yyyy-MM-dd'),
      price: 'RM 55.00',
      bookingCount: 98
    },
    {
      id: '3',
      route: { id: '3', name: 'KL - Malacca Direct' },
      origin: 'Kuala Lumpur',
      destination: 'Malacca',
      departureTime: '10:15 AM',
      date: format(new Date(), 'yyyy-MM-dd'),
      price: 'RM 35.00',
      bookingCount: 76
    },
    {
      id: '4',
      route: { id: '4', name: 'Penang - Ipoh Express' },
      origin: 'Penang',
      destination: 'Ipoh',
      departureTime: '11:00 AM',
      date: format(new Date(), 'yyyy-MM-dd'),
      price: 'RM 30.00',
      bookingCount: 65
    },
    {
      id: '5',
      route: { id: '5', name: 'Johor Bahru - Singapore Shuttle' },
      origin: 'Johor Bahru',
      destination: 'Singapore',
      departureTime: '07:30 AM',
      date: format(new Date(), 'yyyy-MM-dd'),
      price: 'RM 25.00',
      bookingCount: 156
    }
  ];

  useEffect(() => {
    const fetchPopularTrips = async () => {
      setLoading(true);
      try {
        // 尝试从API获取热门行程数据
        const response = await getPopularTrips();
        if (response.success) {
          setTrips(response.data);
        } else {
          console.warn('API返回错误，使用模拟数据:', response.error || 'Failed to fetch popular trips');
          // 使用备用的模拟数据
          setTrips(mockTrips);
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching popular trips:', err);
        console.warn('API调用失败，使用模拟数据');
        // 使用备用的模拟数据
        setTrips(mockTrips);
        setLoading(false);
      }
    };

    fetchPopularTrips();
  }, []);

  const handleTripClick = (trip) => {
    if (onTripSelect) {
      onTripSelect(trip);
    }
  };

  if (loading) {
    return (
      <div className="p-4 text-center">
        <div className="spinner"></div>
        <p className="mt-2 text-gray-600">Loading popular trips...</p>
      </div>
    );
  }

  if (error && trips.length === 0) {
    return (
      <div className="p-4 text-center">
        <div className="text-red-500 mb-2">Error: {error}</div>
        <button 
          className="text-blue-500 underline"
          onClick={() => window.location.reload()}
        >
          Retry
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">Popular Trips</h2>
        <div className="text-sm text-gray-500">
          Showing most booked trips
        </div>
      </div>

      <div className="space-y-4">
        {trips.map((trip) => (
          <div 
            key={trip.id}
            className="card hover:shadow-md transition-shadow cursor-pointer"
            onClick={() => handleTripClick(trip)}
          >
            <div className="flex justify-between items-start">
              <div>
                <h3 className="font-semibold text-lg text-gray-900">{trip.route.name}</h3>
                <div className="flex items-center mt-1 text-sm text-gray-600">
                  <MapPin className="w-4 h-4 mr-1" />
                  <span>{trip.origin} → {trip.destination}</span>
                </div>
              </div>
              <div className="bg-blue-100 text-blue-800 text-xs font-medium px-2.5 py-0.5 rounded-full flex items-center">
                <Star className="w-3 h-3 mr-1" />
                {trip.bookingCount} bookings
              </div>
            </div>

            <div className="flex justify-between mt-4 text-sm">
              <div className="flex items-center text-gray-500">
                <Clock className="w-4 h-4 mr-1" />
                {trip.departureTime}
              </div>
              <div className="flex items-center text-gray-500">
                <Calendar className="w-4 h-4 mr-1" />
                {format(new Date(trip.date), 'dd MMM yyyy')}
              </div>
              <div className="font-medium">{trip.price}</div>
            </div>

            <div className="mt-3 flex justify-end">
              <button className="text-blue-600 text-sm flex items-center hover:text-blue-800">
                View Details
                <ChevronRight className="w-4 h-4 ml-1" />
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default PopularTrips;
