import React, { useState, useEffect } from 'react';
import AdminLayout from '../../layouts/AdminLayout';
import { Search, Calendar, User, CreditCard, ChevronRight, Star } from 'lucide-react';
import { getPopularTrips, searchTrips } from '../../services/adminApi';

const CustomerServicePortal = () => {
  const [activeStep, setActiveStep] = useState(1);
  const [searchParams, setSearchParams] = useState({
    from: '',
    to: '',
    departureDate: '',
    passengers: 1
  });
  const [popularTrips, setPopularTrips] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // 加载热门行程
    const loadPopularTrips = async () => {
      setLoading(true);
      try {
        const response = await getPopularTrips();
        if (response.success) {
          setPopularTrips(response.data);
        }
      } catch (error) {
        console.error('Error loading popular trips:', error);
      } finally {
        setLoading(false);
      }
    };

    loadPopularTrips();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setSearchParams(prev => ({ ...prev, [name]: value }));
  };

  const handlePopularTripSelect = (trip) => {
    setSearchParams({
      from: trip.originName,
      to: trip.destinationName,
      departureDate: new Date().toISOString().split('T')[0], // 默认为今天
      passengers: 1
    });
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    // 实现搜索逻辑
    setActiveStep(2);
  };

  return (
    <AdminLayout>
      <div className="container mx-auto px-4 py-6">
        <h1 className="text-3xl font-bold mb-2">Customer Service Portal</h1>
        <p className="text-gray-600 mb-6">Help walk-in customers purchase tickets quickly and efficiently</p>

        {/* Steps */}
        <div className="flex border-b border-gray-200 mb-6">
          <div className={`flex items-center py-2 px-4 mr-4 border-b-2 ${activeStep === 1 ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}>
            <div className="w-6 h-6 rounded-full bg-blue-100 text-blue-600 flex items-center justify-center mr-2 text-sm font-medium">1</div>
            <span className="font-medium">Search Trips</span>
          </div>
          <div className={`flex items-center py-2 px-4 mr-4 border-b-2 ${activeStep === 2 ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}>
            <div className="w-6 h-6 rounded-full bg-gray-100 text-gray-600 flex items-center justify-center mr-2 text-sm font-medium">2</div>
            <span className="font-medium">Select Trip</span>
          </div>
          <div className={`flex items-center py-2 px-4 mr-4 border-b-2 ${activeStep === 3 ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}>
            <div className="w-6 h-6 rounded-full bg-gray-100 text-gray-600 flex items-center justify-center mr-2 text-sm font-medium">3</div>
            <span className="font-medium">Customer Info</span>
          </div>
          <div className={`flex items-center py-2 px-4 border-b-2 ${activeStep === 4 ? 'border-blue-500 text-blue-600' : 'border-transparent text-gray-500'}`}>
            <div className="w-6 h-6 rounded-full bg-gray-100 text-gray-600 flex items-center justify-center mr-2 text-sm font-medium">4</div>
            <span className="font-medium">Payment</span>
          </div>
        </div>

        {activeStep === 1 && (
          <div>
            <h2 className="text-xl font-semibold mb-4">Search Available Trips</h2>

            {/* Popular Trips */}
            <div className="mb-6">
              <h3 className="text-lg font-medium mb-3 flex items-center">
                <Star className="w-5 h-5 text-yellow-500 mr-2" />
                Popular Trips
              </h3>

              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                {loading ? (
                  <div className="col-span-3 text-center py-4">
                    <div className="spinner"></div>
                    <p className="mt-2 text-gray-600">Loading popular trips...</p>
                  </div>
                ) : popularTrips.length > 0 ? (
                  popularTrips.map((trip, index) => (
                    <div 
                      key={index}
                      className="card hover:shadow-md transition-shadow cursor-pointer"
                      onClick={() => handlePopularTripSelect(trip)}
                    >
                      <div className="flex justify-between items-center">
                        <div>
                          <h4 className="font-medium text-gray-900">{trip.routeName}</h4>
                          <div className="text-sm text-gray-600 mt-1">
                            {trip.originName} → {trip.destinationName}
                          </div>
                          <div className="flex items-center mt-2 text-sm">
                            <div className="flex items-center text-yellow-500">
                              <Star className="w-4 h-4 fill-current" />
                              <span className="ml-1">{trip.avgRating.toFixed(1)}</span>
                            </div>
                            <span className="mx-2 text-gray-300">•</span>
                            <span className="text-gray-500">{trip.bookingsCount} bookings</span>
                          </div>
                        </div>
                        <ChevronRight className="w-5 h-5 text-gray-400" />
                      </div>
                    </div>
                  ))
                ) : (
                  <div className="col-span-3 text-center py-4 text-gray-500">
                    No popular trips found
                  </div>
                )}
              </div>
            </div>

            <div className="card">
              <form onSubmit={handleSearch}>
                <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">From</label>
                    <select
                      name="from"
                      value={searchParams.from}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      required
                    >
                      <option value="">Select origin</option>
                      <option value="Genting Highlands">Genting Highlands</option>
                      <option value="Kuala Lumpur">Kuala Lumpur</option>
                      <option value="Penang">Penang</option>
                      <option value="Johor Bahru">Johor Bahru</option>
                      <option value="Malacca">Malacca</option>
                      <option value="Ipoh">Ipoh</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">To</label>
                    <select
                      name="to"
                      value={searchParams.to}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      required
                    >
                      <option value="">Select destination</option>
                      <option value="Genting Highlands">Genting Highlands</option>
                      <option value="Kuala Lumpur">Kuala Lumpur</option>
                      <option value="Penang">Penang</option>
                      <option value="Johor Bahru">Johor Bahru</option>
                      <option value="Malacca">Malacca</option>
                      <option value="Ipoh">Ipoh</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Departure Date</label>
                    <input
                      type="date"
                      name="departureDate"
                      value={searchParams.departureDate}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      required
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Passengers</label>
                    <select
                      name="passengers"
                      value={searchParams.passengers}
                      onChange={handleInputChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      {[...Array(10)].map((_, i) => (
                        <option key={i} value={i + 1}>
                          {i + 1} {i === 0 ? 'passenger' : 'passengers'}
                        </option>
                      ))}
                    </select>
                  </div>
                </div>

                <div className="mt-6">
                  <button type="submit" className="btn-primary w-full md:w-auto">
                    <Search className="w-4 h-4 mr-2" />
                    Search Trips
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {activeStep === 2 && (
          <div>
            <h2 className="text-xl font-semibold mb-4">Select Trip</h2>
            {/* Trip selection content */}
          </div>
        )}

        {activeStep === 3 && (
          <div>
            <h2 className="text-xl font-semibold mb-4">Customer Information</h2>
            {/* Customer info content */}
          </div>
        )}

        {activeStep === 4 && (
          <div>
            <h2 className="text-xl font-semibold mb-4">Payment</h2>
            {/* Payment content */}
          </div>
        )}
      </div>
    </AdminLayout>
  );
};

export default CustomerServicePortal;
