import React, { useState, useEffect } from 'react';
import AdminLayout from '../../layouts/AdminLayout';
import { 
  getRevenueAnalytics, 
  getOccupancyAnalytics, 
  getRouteAnalytics, 
  getPopularTrips 
} from '../../services/adminApi';
import { 
  Chart as ChartJS, 
  CategoryScale, 
  LinearScale, 
  PointElement, 
  LineElement, 
  BarElement, 
  ArcElement, 
  Title, 
  Tooltip, 
  Legend 
} from 'chart.js';
import { Line, Bar, Doughnut } from 'react-chartjs-2';
import { format, subDays, startOfMonth, endOfMonth, eachDayOfInterval } from 'date-fns';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const AdminAnalytics = () => {
  const [timeRange, setTimeRange] = useState('week');
  const [revenueData, setRevenueData] = useState(null);
  const [occupancyData, setOccupancyData] = useState(null);
  const [routeData, setRouteData] = useState(null);
  const [popularTrips, setPopularTrips] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAnalytics = async () => {
      try {
        setLoading(true);

        let startDate, endDate;
        const today = new Date();

        switch (timeRange) {
          case 'week':
            startDate = format(subDays(today, 7), 'yyyy-MM-dd');
            endDate = format(today, 'yyyy-MM-dd');
            break;
          case 'month':
            startDate = format(startOfMonth(today), 'yyyy-MM-dd');
            endDate = format(endOfMonth(today), 'yyyy-MM-dd');
            break;
          case 'quarter':
            startDate = format(subDays(today, 90), 'yyyy-MM-dd');
            endDate = format(today, 'yyyy-MM-dd');
            break;
          default:
            startDate = format(subDays(today, 7), 'yyyy-MM-dd');
            endDate = format(today, 'yyyy-MM-dd');
        }

        const [revenue, occupancy, routes, trips] = await Promise.all([
          getRevenueAnalytics(startDate, endDate),
          getOccupancyAnalytics(startDate, endDate),
          getRouteAnalytics(startDate, endDate),
          getPopularTrips(startDate, endDate, 5)
        ]);

        setRevenueData(revenue);
        setOccupancyData(occupancy);
        setRouteData(routes);
        setPopularTrips(trips);
      } catch (error) {
        console.error('Error fetching analytics:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchAnalytics();
  }, [timeRange]);

  const prepareRevenueChartData = () => {
    if (!revenueData) return null;

    return {
      labels: revenueData.map(item => format(new Date(item.date), 'MMM dd')),
      datasets: [
        {
          label: 'Daily Revenue',
          data: revenueData.map(item => item.amount),
          borderColor: 'rgb(53, 162, 235)',
          backgroundColor: 'rgba(53, 162, 235, 0.5)',
          tension: 0.3,
        },
      ],
    };
  };

  const prepareOccupancyChartData = () => {
    if (!occupancyData) return null;

    return {
      labels: occupancyData.map(item => format(new Date(item.date), 'MMM dd')),
      datasets: [
        {
          label: 'Average Occupancy Rate (%)',
          data: occupancyData.map(item => (item.occupancyRate * 100).toFixed(1)),
          backgroundColor: 'rgba(75, 192, 192, 0.6)',
        },
      ],
    };
  };

  const prepareRouteChartData = () => {
    if (!routeData) return null;

    return {
      labels: routeData.map(item => `${item.origin} - ${item.destination}`),
      datasets: [
        {
          label: 'Number of Trips',
          data: routeData.map(item => item.tripCount),
          backgroundColor: [
            'rgba(255, 99, 132, 0.6)',
            'rgba(54, 162, 235, 0.6)',
            'rgba(255, 206, 86, 0.6)',
            'rgba(75, 192, 192, 0.6)',
            'rgba(153, 102, 255, 0.6)',
          ],
          borderWidth: 1,
        },
      ],
    };
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-MY', {
      style: 'currency',
      currency: 'MYR',
    }).format(amount);
  };

  const calculateTotalRevenue = () => {
    if (!revenueData) return 0;
    return revenueData.reduce((sum, item) => sum + item.amount, 0);
  };

  const calculateAverageOccupancy = () => {
    if (!occupancyData || occupancyData.length === 0) return 0;
    const sum = occupancyData.reduce((total, item) => total + item.occupancyRate, 0);
    return ((sum / occupancyData.length) * 100).toFixed(1);
  };

  const calculateTotalTrips = () => {
    if (!routeData) return 0;
    return routeData.reduce((sum, item) => sum + item.tripCount, 0);
  };

  return (
    <AdminLayout>
      <div className="container mx-auto px-4 py-6">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-2xl font-bold">Analytics Dashboard</h1>
          <div className="flex space-x-2">
            <button 
              onClick={() => setTimeRange('week')}
              className={`px-4 py-2 rounded ${timeRange === 'week' 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-200 hover:bg-gray-300'}`}
            >
              Week
            </button>
            <button 
              onClick={() => setTimeRange('month')}
              className={`px-4 py-2 rounded ${timeRange === 'month' 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-200 hover:bg-gray-300'}`}
            >
              Month
            </button>
            <button 
              onClick={() => setTimeRange('quarter')}
              className={`px-4 py-2 rounded ${timeRange === 'quarter' 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-200 hover:bg-gray-300'}`}
            >
              Quarter
            </button>
          </div>
        </div>

        {loading ? (
          <div className="flex justify-center items-center h-64">
            <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
          </div>
        ) : (
          <>
            {/* Summary Cards */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">Total Revenue</h3>
                <p className="text-3xl font-bold text-blue-600">{formatCurrency(calculateTotalRevenue())}</p>
                <p className="text-sm text-gray-500 mt-2">For the selected period</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">Average Occupancy</h3>
                <p className="text-3xl font-bold text-green-600">{calculateAverageOccupancy()}%</p>
                <p className="text-sm text-gray-500 mt-2">Seats filled across all trips</p>
              </div>
              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold text-gray-700 mb-2">Total Trips</h3>
                <p className="text-3xl font-bold text-purple-600">{calculateTotalTrips()}</p>
                <p className="text-sm text-gray-500 mt-2">Trips completed in this period</p>
              </div>
            </div>

            {/* Charts */}
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 mb-6">
              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Revenue Trend</h3>
                {revenueData && revenueData.length > 0 ? (
                  <Line 
                    data={prepareRevenueChartData()} 
                    options={{
                      responsive: true,
                      plugins: {
                        legend: {
                          position: 'top',
                        },
                        title: {
                          display: false,
                        },
                      },
                      scales: {
                        y: {
                          beginAtZero: true,
                          ticks: {
                            callback: function(value) {
                              return 'RM ' + value;
                            }
                          }
                        }
                      }
                    }}
                  />
                ) : (
                  <div className="flex justify-center items-center h-64 bg-gray-50 rounded">
                    <p className="text-gray-500">No revenue data available for the selected period</p>
                  </div>
                )}
              </div>

              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Occupancy Rates</h3>
                {occupancyData && occupancyData.length > 0 ? (
                  <Bar 
                    data={prepareOccupancyChartData()} 
                    options={{
                      responsive: true,
                      plugins: {
                        legend: {
                          position: 'top',
                        },
                        title: {
                          display: false,
                        },
                      },
                      scales: {
                        y: {
                          beginAtZero: true,
                          max: 100,
                          ticks: {
                            callback: function(value) {
                              return value + '%';
                            }
                          }
                        }
                      }
                    }}
                  />
                ) : (
                  <div className="flex justify-center items-center h-64 bg-gray-50 rounded">
                    <p className="text-gray-500">No occupancy data available for the selected period</p>
                  </div>
                )}
              </div>
            </div>

            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Popular Routes</h3>
                {routeData && routeData.length > 0 ? (
                  <div className="h-80">
                    <Doughnut 
                      data={prepareRouteChartData()} 
                      options={{
                        responsive: true,
                        maintainAspectRatio: false,
                        plugins: {
                          legend: {
                            position: 'right',
                          },
                          title: {
                            display: false,
                          },
                        },
                      }}
                    />
                  </div>
                ) : (
                  <div className="flex justify-center items-center h-64 bg-gray-50 rounded">
                    <p className="text-gray-500">No route data available for the selected period</p>
                  </div>
                )}
              </div>

              <div className="bg-white p-6 rounded-lg shadow-md">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Top Performing Trips</h3>
                {popularTrips && popularTrips.length > 0 ? (
                  <div className="overflow-x-auto">
                    <table className="min-w-full divide-y divide-gray-200">
                      <thead className="bg-gray-50">
                        <tr>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Route</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Bookings</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Revenue</th>
                          <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Occupancy</th>
                        </tr>
                      </thead>
                      <tbody className="bg-white divide-y divide-gray-200">
                        {popularTrips.map((trip, index) => (
                          <tr key={index}>
                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                              {trip.origin} to {trip.destination}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                              {trip.bookingCount}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                              {formatCurrency(trip.revenue)}
                            </td>
                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                              <div className="flex items-center">
                                <div className="w-full bg-gray-200 rounded-full h-2.5">
                                  <div 
                                    className="bg-blue-600 h-2.5 rounded-full" 
                                    style={{ width: `${(trip.occupancyRate * 100).toFixed(1)}%` }}
                                  ></div>
                                </div>
                                <span className="ml-2">{(trip.occupancyRate * 100).toFixed(1)}%</span>
                              </div>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                ) : (
                  <div className="flex justify-center items-center h-64 bg-gray-50 rounded">
                    <p className="text-gray-500">No trip data available for the selected period</p>
                  </div>
                )}
              </div>
            </div>
          </>
        )}
      </div>
    </AdminLayout>
  );
};

export default AdminAnalytics;
