import React, { useState, useEffect } from 'react';
import AdminLayout from '../../layouts/AdminLayout';
import { Calendar, MapPin, Bus, Clock, ArrowRight } from 'lucide-react';
import { getAllLocations, getAllRoutes, getAllVehicles, createSchedule } from '../../services/adminApi';

const CreateSchedule = () => {
  const [step, setStep] = useState(1);
  const [loading, setLoading] = useState(true);
  const [locations, setLocations] = useState([]);
  const [routes, setRoutes] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [selectedRoute, setSelectedRoute] = useState(null);
  const [customRouteMode, setCustomRouteMode] = useState(false);

  const [formData, setFormData] = useState({
    routeId: '',
    vehicleId: '',
    departureDate: new Date().toISOString().split('T')[0],
    departureTime: '08:00',
    basePrice: 0,
    selectedStops: [],
    seatPricing: {
      regular: { multiplier: 1.0, enabled: true },
      premium: { multiplier: 1.5, enabled: true },
      vip: { multiplier: 2.0, enabled: true }
    },
    pricingRules: {
      weekend: { percentage: 15, enabled: false },
      holiday: { percentage: 25, enabled: false },
      earlyBooking: { percentage: 10, enabled: false }
    }
  });

  // 加载数据
  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      try {
        const [locationsRes, routesRes, vehiclesRes] = await Promise.all([
          getAllLocations(),
          getAllRoutes(),
          getAllVehicles()
        ]);

        if (locationsRes.success) setLocations(locationsRes.data);
        if (routesRes.success) setRoutes(routesRes.data);
        if (vehiclesRes.success) setVehicles(vehiclesRes.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  // 处理路线选择
  const handleRouteChange = (e) => {
    const routeId = e.target.value;
    setFormData(prev => ({ ...prev, routeId }));

    if (routeId) {
      const route = routes.find(r => r.id === routeId);
      if (route) {
        setSelectedRoute(route);

        // 设置路线的站点
        const routeStops = route.stops?.map(stop => {
          const location = locations.find(l => l.id === stop.locationId);
          return {
            locationId: stop.locationId,
            name: location?.name || 'Unknown',
            city: location?.city || '',
            isOrigin: stop.isOrigin,
            isDestination: stop.isDestination,
            arrivalOffsetMinutes: stop.arrivalOffsetMinutes,
            departureOffsetMinutes: stop.departureOffsetMinutes,
            priceFromOrigin: stop.priceFromOrigin
          };
        }) || [];

        setFormData(prev => ({
          ...prev,
          basePrice: route.basePrice || 0,
          selectedStops: routeStops
        }));
      }
    } else {
      setSelectedRoute(null);
      setFormData(prev => ({
        ...prev,
        basePrice: 0,
        selectedStops: []
      }));
    }
  };

  // 切换自定义路线模式
  const toggleCustomRouteMode = () => {
    setCustomRouteMode(!customRouteMode);
    if (!customRouteMode) {
      // 切换到自定义模式时清空已选路线
      setFormData(prev => ({
        ...prev,
        routeId: '',
        selectedStops: []
      }));
      setSelectedRoute(null);
    }
  };

  // 添加站点到路线
  const addStopToRoute = (locationId) => {
    const location = locations.find(l => l.id === locationId);
    if (!location) return;

    const newStop = {
      locationId,
      name: location.name,
      city: location.city,
      isOrigin: formData.selectedStops.length === 0,
      isDestination: false,
      arrivalOffsetMinutes: formData.selectedStops.length === 0 ? 0 : 
        formData.selectedStops[formData.selectedStops.length - 1].departureOffsetMinutes + 60,
      departureOffsetMinutes: formData.selectedStops.length === 0 ? 10 : 
        formData.selectedStops[formData.selectedStops.length - 1].departureOffsetMinutes + 70,
      priceFromOrigin: formData.selectedStops.length === 0 ? 0 : 
        formData.basePrice * (formData.selectedStops.length * 0.5)
    };

    setFormData(prev => ({
      ...prev,
      selectedStops: [...prev.selectedStops, newStop]
    }));
  };

  // 设置站点为终点站
  const setStopAsDestination = (index) => {
    setFormData(prev => {
      const updatedStops = prev.selectedStops.map((stop, i) => ({
        ...stop,
        isDestination: i === index
      }));
      return { ...prev, selectedStops: updatedStops };
    });
  };

  // 从路线中移除站点
  const removeStop = (index) => {
    setFormData(prev => {
      const updatedStops = [...prev.selectedStops];
      updatedStops.splice(index, 1);

      // 如果删除了起点，将下一个站点设为起点
      if (index === 0 && updatedStops.length > 0) {
        updatedStops[0] = { ...updatedStops[0], isOrigin: true, priceFromOrigin: 0 };
      }

      // 如果删除了终点，清除终点标记
      const hadDestination = prev.selectedStops.some(s => s.isDestination);
      if (hadDestination && !updatedStops.some(s => s.isDestination) && updatedStops.length > 0) {
        updatedStops[updatedStops.length - 1] = {
          ...updatedStops[updatedStops.length - 1],
          isDestination: true
        };
      }

      return { ...prev, selectedStops: updatedStops };
    });
  };

  // 更新站点信息
  const updateStopInfo = (index, field, value) => {
    setFormData(prev => {
      const updatedStops = [...prev.selectedStops];
      updatedStops[index] = { ...updatedStops[index], [field]: value };
      return { ...prev, selectedStops: updatedStops };
    });
  };

  // 处理表单输入变化
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name.includes('.')) {
      const [section, subsection, field] = name.split('.');
      setFormData(prev => ({
        ...prev,
        [section]: {
          ...prev[section],
          [subsection]: {
            ...prev[section][subsection],
            [field]: type === 'checkbox' ? checked : value
          }
        }
      }));
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: type === 'checkbox' ? checked : value
      }));
    }
  };

  // 提交表单
  const handleSubmit = async (e) => {
    e.preventDefault();

    // 验证表单
    if (!formData.vehicleId) {
      alert('Please select a vehicle');
      return;
    }

    if (formData.selectedStops.length < 2) {
      alert('Trip must have at least two stops (origin and destination)');
      return;
    }

    if (!formData.selectedStops.some(stop => stop.isOrigin)) {
      alert('Trip must have an origin stop');
      return;
    }

    if (!formData.selectedStops.some(stop => stop.isDestination)) {
      alert('Trip must have a destination stop');
      return;
    }

    // 准备提交数据
    const submitData = {
      ...formData,
      departureDateTime: `${formData.departureDate}T${formData.departureTime}:00`
    };

    setLoading(true);
    try {
      const response = await createSchedule(submitData);
      if (response.success) {
        alert('Trip scheduled successfully!');
        // 重置表单或导航到其他页面
        window.location.href = '/admin/schedule';
      } else {
        alert(`Failed to schedule trip: ${response.message || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Error scheduling trip:', error);
      alert('An error occurred while scheduling the trip');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <AdminLayout>
        <div className="container mx-auto px-4 py-6">
          <div className="text-center py-10">
            <div className="spinner"></div>
            <p className="mt-2 text-gray-600">Loading data...</p>
          </div>
        </div>
      </AdminLayout>
    );
  }

  return (
    <AdminLayout>
      <div className="container mx-auto px-4 py-6">
        <h1 className="text-3xl font-bold mb-6">Create New Trip Schedule</h1>

        <div className="card">
          <form onSubmit={handleSubmit} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {/* 左侧：基本信息 */}
              <div className="space-y-6">
                <h2 className="text-xl font-semibold">Basic Trip Information</h2>

                <div className="flex items-center justify-between mb-4">
                  <h3 className="font-medium">Route Selection</h3>
                  <button
                    type="button"
                    onClick={toggleCustomRouteMode}
                    className="text-sm text-blue-600 hover:text-blue-800"
                  >
                    {customRouteMode ? 'Select Existing Route' : 'Create Custom Route'}
                  </button>
                </div>

                {!customRouteMode ? (
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Route
                    </label>
                    <select
                      name="routeId"
                      value={formData.routeId}
                      onChange={handleRouteChange}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    >
                      <option value="">Select a route</option>
                      {routes.map(route => (
                        <option key={route.id} value={route.id}>
                          {route.name}
                        </option>
                      ))}
                    </select>

                    {selectedRoute && (
                      <div className="mt-2 text-sm text-gray-600">
                        <div className="flex items-center">
                          <MapPin className="w-4 h-4 mr-1" />
                          <span>
                            {selectedRoute.originLocation?.name} → {selectedRoute.destinationLocation?.name}
                          </span>
                        </div>
                        <div className="flex items-center mt-1">
                          <span>Base Price: RM {parseFloat(selectedRoute.basePrice).toFixed(2)}</span>
                        </div>
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Route Name
                      </label>
                      <input
                        type="text"
                        name="routeName"
                        value={formData.routeName || ''}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        placeholder="e.g. KL Express Special"
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-1">
                        Base Price (RM)
                      </label>
                      <input
                        type="number"
                        name="basePrice"
                        value={formData.basePrice}
                        onChange={handleInputChange}
                        min="0"
                        step="0.01"
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700 mb-2">
                        Add Stops
                      </label>
                      <select
                        onChange={(e) => {
                          addStopToRoute(e.target.value);
                          e.target.value = '';
                        }}
                        value=""
                        className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                      >
                        <option value="">Select a location to add</option>
                        {locations.map(location => (
                          <option key={location.id} value={location.id}>
                            {location.name} ({location.city})
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="border rounded-md p-4">
                      <h4 className="font-medium mb-2">Selected Stops</h4>
                      {formData.selectedStops.length === 0 ? (
                        <p className="text-sm text-gray-500">No stops selected yet. Add stops to create your route.</p>
                      ) : (
                        <div className="space-y-3">
                          {formData.selectedStops.map((stop, index) => (
                            <div key={index} className="flex items-start justify-between border-b pb-2">
                              <div className="flex items-start">
                                <div className={`w-6 h-6 rounded-full flex items-center justify-center mr-2 ${
                                  stop.isOrigin ? 'bg-green-100 text-green-600' : 
                                  stop.isDestination ? 'bg-red-100 text-red-600' : 
                                  'bg-blue-100 text-blue-600'
                                }`}>
                                  {index + 1}
                                </div>
                                <div>
                                  <div className="font-medium">{stop.name}</div>
                                  <div className="text-xs text-gray-500">
                                    {stop.isOrigin ? 'Origin' : stop.isDestination ? 'Destination' : 'Stop'}
                                  </div>
                                  <div className="flex items-center text-xs text-gray-500 mt-1">
                                    <Clock className="w-3 h-3 mr-1" />
                                    {stop.isOrigin ? (
                                      <span>Departs at +{stop.departureOffsetMinutes} min</span>
                                    ) : stop.isDestination ? (
                                      <span>Arrives at +{stop.arrivalOffsetMinutes} min</span>
                                    ) : (
                                      <span>Arr: +{stop.arrivalOffsetMinutes} min, Dep: +{stop.departureOffsetMinutes} min</span>
                                    )}
                                  </div>
                                </div>
                              </div>
                              <div className="flex space-x-2">
                                {!stop.isOrigin && !stop.isDestination && (
                                  <button
                                    type="button"
                                    onClick={() => setStopAsDestination(index)}
                                    className="text-xs text-blue-600 hover:text-blue-800"
                                  >
                                    Set as destination
                                  </button>
                                )}
                                <button
                                  type="button"
                                  onClick={() => removeStop(index)}
                                  className="text-xs text-red-600 hover:text-red-800"
                                >
                                  Remove
                                </button>
                              </div>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  </div>
                )}

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Vehicle
                  </label>
                  <select
                    name="vehicleId"
                    value={formData.vehicleId}
                    onChange={handleInputChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    required
                  >
                    <option value="">Select a vehicle</option>
                    {vehicles.map(vehicle => (
                      <option key={vehicle.id} value={vehicle.id}>
                        {vehicle.registrationNumber} - {vehicle.model} ({vehicle.capacity} seats)
                      </option>
                    ))}
                  </select>
                </div>

                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Departure Date
                    </label>
                    <div className="relative">
                      <input
                        type="date"
                        name="departureDate"
                        value={formData.departureDate}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 pl-10 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        required
                      />
                      <Calendar className="w-5 h-5 text-gray-400 absolute left-3 top-2.5" />
                    </div>
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Departure Time
                    </label>
                    <div className="relative">
                      <input
                        type="time"
                        name="departureTime"
                        value={formData.departureTime}
                        onChange={handleInputChange}
                        className="w-full px-3 py-2 pl-10 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        required
                      />
                      <Clock className="w-5 h-5 text-gray-400 absolute left-3 top-2.5" />
                    </div>
                  </div>
                </div>
              </div>

              {/* 右侧：价格配置 */}
              <div className="space-y-6">
                <h2 className="text-xl font-semibold">Pricing Configuration</h2>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Base Price (RM)
                  </label>
                  <input
                    type="number"
                    name="basePrice"
                    value={formData.basePrice}
                    onChange={handleInputChange}
                    min="0"
                    step="0.01"
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    disabled={!customRouteMode && formData.routeId}
                  />
                </div>

                <div>
                  <h3 className="font-medium mb-2">Seat Type Pricing</h3>
                  <div className="space-y-3">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="regular-seats"
                          name="seatPricing.regular.enabled"
                          checked={formData.seatPricing.regular.enabled}
                          onChange={handleInputChange}
                          className="mr-2"
                        />
                        <label htmlFor="regular-seats" className="text-sm">Regular Seats</label>
                      </div>
                      <div className="flex items-center">
                        <span className="text-sm mr-2">Multiplier:</span>
                        <input
                          type="number"
                          name="seatPricing.regular.multiplier"
                          value={formData.seatPricing.regular.multiplier}
                          onChange={handleInputChange}
                          min="0.1"
                          step="0.1"
                          className="w-16 px-2 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        />
                        <span className="text-sm ml-1">x</span>
                      </div>
                    </div>

                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="premium-seats"
                          name="seatPricing.premium.enabled"
                          checked={formData.seatPricing.premium.enabled}
                          onChange={handleInputChange}
                          className="mr-2"
                        />
                        <label htmlFor="premium-seats" className="text-sm">Premium Seats</label>
                      </div>
                      <div className="flex items-center">
                        <span className="text-sm mr-2">Multiplier:</span>
                        <input
                          type="number"
                          name="seatPricing.premium.multiplier"
                          value={formData.seatPricing.premium.multiplier}
                          onChange={handleInputChange}
                          min="0.1"
                          step="0.1"
                          className="w-16 px-2 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        />
                        <span className="text-sm ml-1">x</span>
                      </div>
                    </div>

                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="vip-seats"
                          name="seatPricing.vip.enabled"
                          checked={formData.seatPricing.vip.enabled}
                          onChange={handleInputChange}
                          className="mr-2"
                        />
                        <label htmlFor="vip-seats" className="text-sm">VIP Seats</label>
                      </div>
                      <div className="flex items-center">
                        <span className="text-sm mr-2">Multiplier:</span>
                        <input
                          type="number"
                          name="seatPricing.vip.multiplier"
                          value={formData.seatPricing.vip.multiplier}
                          onChange={handleInputChange}
                          min="0.1"
                          step="0.1"
                          className="w-16 px-2 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        />
                        <span className="text-sm ml-1">x</span>
                      </div>
                    </div>
                  </div>
                </div>

                <div>
                  <h3 className="font-medium mb-2">Dynamic Pricing Rules</h3>
                  <div className="space-y-3">
                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="weekend-pricing"
                          name="pricingRules.weekend.enabled"
                          checked={formData.pricingRules.weekend.enabled}
                          onChange={handleInputChange}
                          className="mr-2"
                        />
                        <label htmlFor="weekend-pricing" className="text-sm">Weekend Pricing</label>
                      </div>
                      <div className="flex items-center">
                        <span className="text-sm mr-2">+</span>
                        <input
                          type="number"
                          name="pricingRules.weekend.percentage"
                          value={formData.pricingRules.weekend.percentage}
                          onChange={handleInputChange}
                          min="0"
                          className="w-16 px-2 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        />
                        <span className="text-sm ml-1">%</span>
                      </div>
                    </div>

                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="holiday-pricing"
                          name="pricingRules.holiday.enabled"
                          checked={formData.pricingRules.holiday.enabled}
                          onChange={handleInputChange}
                          className="mr-2"
                        />
                        <label htmlFor="holiday-pricing" className="text-sm">Holiday Pricing</label>
                      </div>
                      <div className="flex items-center">
                        <span className="text-sm mr-2">+</span>
                        <input
                          type="number"
                          name="pricingRules.holiday.percentage"
                          value={formData.pricingRules.holiday.percentage}
                          onChange={handleInputChange}
                          min="0"
                          className="w-16 px-2 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        />
                        <span className="text-sm ml-1">%</span>
                      </div>
                    </div>

                    <div className="flex items-center justify-between">
                      <div className="flex items-center">
                        <input
                          type="checkbox"
                          id="early-booking"
                          name="pricingRules.earlyBooking.enabled"
                          checked={formData.pricingRules.earlyBooking.enabled}
                          onChange={handleInputChange}
                          className="mr-2"
                        />
                        <label htmlFor="early-booking" className="text-sm">Early Booking Discount</label>
                      </div>
                      <div className="flex items-center">
                        <span className="text-sm mr-2">-</span>
                        <input
                          type="number"
                          name="pricingRules.earlyBooking.percentage"
                          value={formData.pricingRules.earlyBooking.percentage}
                          onChange={handleInputChange}
                          min="0"
                          className="w-16 px-2 py-1 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                        />
                        <span className="text-sm ml-1">%</span>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="flex justify-end space-x-4 pt-4 border-t">
              <button
                type="button"
                onClick={() => window.history.back()}
                className="btn-secondary"
              >
                Cancel
              </button>
              <button
                type="submit"
                className="btn-primary"
                disabled={loading}
              >
                {loading ? 'Processing...' : 'Schedule Trip'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </AdminLayout>
  );
};

export default CreateSchedule;
